function ed_CARPS_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getCodeMaster";		
	var sMethodName = "service::pscomlib/selectMST050PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " GRPCD=" + quote("CAR");
		sArgument  += " MSTCD=" + quote(ed_CARPS.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST050PF.GetRowCount() < 1){
		alert(setPopMessage("4665"));
		setMessage("4665");	
		ed_CARPS.SetFocus();
		return;
	}
	st_CARPS.Text=ds_MST050PF.GetColumn(0,"MSTSM");
	http.usewaitcursor = true;		
}

function getOGMPrtno(tVal)
{
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectBM008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM008PR=ds_BM008PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " ITNBR=" + quote(tVal);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_BM008PR.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}	
}

function ds_SOMC010_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="IORDT"){
		if(varNewValue != "D" && varNewValue != "E" && varNewValue != "K"){
				alert(setPopMessage("4692"));
				setMessage("4692");	
			return false;
		}
	}

	if( strColumnID == "PSPNO" ){
		if(varNewValue != "" ){
			var tval=getOGMPrtno(toUpper(varNewValue));
			if(tval==0){
				alert(setPopMessage("4001"));
				setMessage("4001");	
				return false;
			}
		}
	}

	if(strColumnID=="IALYR"){
		if(parseInt(varNewValue) > 100 || parseInt(varNewValue) < 1 ){
				alert(setPopMessage("4693"));
				setMessage("4693");	
			return false;
		}
	}

	if(strColumnID=="IDISR"){
		if(parseInt(varNewValue) > 100 || parseInt(varNewValue) < 1 ){
				alert(setPopMessage("4694"));
				setMessage("4694");	
			return false;
		}
	}

	if(strColumnID=="IQTYP"){
		if(varNewValue == 0 || varNewValue == ""){
				alert(setPopMessage("4695"));
				setMessage("4695");	
			return false;
		}
	}
}

function ds_SOMC010_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "PSPNO" ){
		if(varNewValue != "" ){
			ds_SOMC010.SetColumn(nRow,"PSPNO",toUpper(varNewValue));
			ds_SOMC010.SetColumn(nRow,"ITDSC",ds_BM008PR.GetColumn(0,"ITDSC"));
		}	
	}	
}
