function ds_SOMC010_CanRowPosChange(obj,nOldRow,nRow)
{
	if(ds_SOMC010.GetRowType(ds_SOMC010.currow)=="update" ||ds_SOMC010.GetRowType(ds_SOMC010.currow)=="insert" ){

		var pspno=ds_SOMC010.GetColumn(ds_SOMC010.currow,"PSPNO");
		var iordt=ds_SOMC010.GetColumn(ds_SOMC010.currow,"IORDT");
		var ialyr=ds_SOMC010.GetColumn(ds_SOMC010.currow,"IALYR");
		var idisr=ds_SOMC010.GetColumn(ds_SOMC010.currow,"IDISR");
		var iqtyp=ds_SOMC010.GetColumn(ds_SOMC010.currow,"IQTYP");
		var frdt=ds_SOMC010.GetColumn(ds_SOMC010.currow,"IFRDT");
		var todt=ds_SOMC010.GetColumn(ds_SOMC010.currow,"ITODT");
		if(length(pspno)!=0){
			if(length(iordt)==0){
				alert(setPopMessage("4688"));
				setMessage("4688");	
				return false;
			}
			if(length(ialyr)==0){
				alert(setPopMessage("4689"));
				setMessage("4689");	
				return false;
			}
			if(length(idisr)==0){
				alert(setPopMessage("4690"));
				setMessage("4690");	
				return false;
			}
			if(length(iqtyp)==0){
				alert(setPopMessage("4691"));
				setMessage("4691");	
				return false;
			}
			if(frdt > todt){
				alert(setPopMessage("1503"));
				setMessage("1503");	
				return false;
			}												
		}	
	}
	return true;
}

