function setExcel()
{
	var fName   = "일일 수주이관전 ERROR LIST";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|일일 수주이관전 ERROR LIST";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

