function fn_Search()
{
	if(ed_pyvnd.GetLength()=0 || ed_pyvnd.GetLength()!=4){
		alert("4자리 업체코드를 입력하십시요.");
	} else {
		if(Edit1.GetLength()=0 || Edit1.GetLength()!=2){
			alert("2자리 SHIP TO 코드를 입력하십시요.");	
		} else {
			getSPME114Q();
		}
	}	
}

