function detail_View(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_SPYC902Q.rowcount <= 0 || ds_SPYC902Q.GetRowType(ds_SPYC902Q.row) != 'normal') return ;
	var plantcd;
	if(cb_plant.Value == "") {
		plantcd="ZZ";
	}else {
		plantcd=cb_plant.Value;
	}
	var str = dialog("PSSALLIB::SPYC902QD.xml","P_COMPS=" + ds_SPYC902Q.GetColumn(ds_SPYC902Q.row,"G") + " " + 
	                                           "P_PLTCD=" + plantcd + " " + 
	                                           "P_PRDCD=" + ds_SPYC902Q.GetColumn(ds_SPYC902Q.row,"PRDCD") + " " + 
	                                           "P_YYMM=" + SubStr(ca_plnym.Value,0,6) + " " +
	                                           "P_PLNGB=" + cb_plngb.Value,1100,600);
	trace (str);
}

function Button0_OnClick(obj)
{
	detail_View(obj);
}

