function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	Static6.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");
	Static16.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");	
	Static8.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");	
	Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");	
	Static2.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");	
	Static4.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");	
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_COMP=ds_ComCode_COMP";
	var sArgument   = "";
		sArgument   += " CLASS='COMP'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	ds_VComCode_COMP.Copy("ds_ComCode_COMP");

	ds_VComCode_COMP.InsertRow(0);
	ds_VComCode_COMP.SetColumn(0,"CODE1","");
	ds_VComCode_COMP.SetColumn(0,"CODE_NAME","[전체]");


	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	ds_VComCode_PLNT.InsertRow(0);
	ds_VComCode_PLNT.SetColumn(0,"CODE2","");
	ds_VComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_searchCodeName1.index = 0;
	cb_searchCodeName2.index = 0;	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSPYC908Q(){
	var sKind		= "getSPYC908Q";						
	var sMethodName = "service::pssallib/selectSPYC908Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPYC908Q=ds_SPYC908Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@PLTCD=" + quote(cb_searchCodeName2.Value);
	sArgument += "@PLNGB=" + quote("1");
	sArgument += "@PLNYM1=" + quote(SubStr(Calendar0.Value,0,6));
	sArgument += "@PLNYM2=" + quote(SubStr(AddMonth(Calendar0.Value,11),0,6));	
	sArgument += "@PLNYM3=" + quote(SubStr(Calendar1.Value,0,6));
	sArgument += "@PLNYM4=" + quote(SubStr(AddMonth(Calendar1.Value,11),0,6));	
	sArgument += "@JPSDT1=" + quote(SubStr(Calendar2.Value,0,6));
	sArgument += "@JPSDT2=" + quote(SubStr(AddMonth(Calendar2.Value,11),0,6));
	sArgument += "@PYVND1=" + quote(Edit0.Value);
	sArgument += "@PYVND2=" + quote(Edit1.Value);	

	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "년판매계획대 실적집계조회 업체";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|년판매계획대 실적집계조회 업체";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getSPYC908Q"){
		// 조회건수가 없을경우
		if(ds_SPYC908Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

function detail_View(obj)
{
	if(ds_SPYC908Q.rowcount <= 0 || ds_SPYC908Q.GetRowType(ds_SPYC908Q.row) != 'normal') return ;
	var str = dialog("PSSALLIB::SPYC908QD.xml","PYVND=" + ds_SPYC908Q.GetColumn(ds_SPYC908Q.row,"V"),1100,600);
}

