/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "대우 발주정보현황 ERROR LIST";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|대우 발주정보현황 ERROR LIST";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

