function getComCode(){
	if (comps == "E1"){
		cb_searchCodeName1.index = 1;
	}else{
		cb_searchCodeName1.index = 0;
	}
}

function getMst040()
{
	var groupCOMPS;
	if (cb_searchCodeName1.Value == "S"){
		groupCOMPS = "C1";
	}else if(cb_searchCodeName1.Value == "E"){
		groupCOMPS = "E1";
	}else{
		groupCOMPS = "XX";
	}
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(groupCOMPS);
	sArgument += " MAJCD=" + quote("SMB120PFMAGAM");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	next_month = AddMonth(ds_MST040PF.GetColumn(0,"MJINT")+"01",1);
	cal_JSOYM.Value = next_month;
	//alert(next_month);
}

