	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVM020PF=ds_SVM020PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " VNDCD=" + quote(vndcd);
	sArgument += " FACCD=" + quote(faccd);
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;

	//alert(next_month);
}
