	var sMethodName = "service::pssallib/insertSVHE314UC.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_DFACD=ds_DFACD ds_SVHE314UC=ds_SVHE314UC:A";         		// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVHE314UC_CHECK=ds_SVHE314UC_CHECK"; 					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
	div_ing.Visible = false;
	SetWaitCursor(false);
}
