}
/********************************************************************************************************** 
 * 내용설명 : host up
 * 주요변수 : 
 * ERPPC.CSV --> ds_SVHE324UC_0 --> ds_SVHE324UC_1(거래처,품번생성) --> ds_SVHE324UC(집계)--> svh330pf
 *                              --> ds_SVHE324UC_2(error File)
 **********************************************************************************************************/ 
function setSVHE324UC(){
	SetTimer(1,100);
	div_ing.Visible = true;

	//SetWaitCursor(true);
	ds_SVHE324UC_0.DeleteAll();
	ds_SVHE324UC_1.ClearData();
	ds_SVHE324UC_2.ClearData();
	ds_SVHE324UC.ClearData();
	ds_DFACD.ClearData();

	var file_name = "ERPP7.CSV";
	var file_path = "C:\\hkc\\van";

	var path = file_path + "\\" + file_name;

	setMessageDirect(path);	
	Ext_Csv2Ds(path,"ds_SVHE324UC_0",false,setColumn(38),",");

	//Grid324UC_0.CreateFormat();

	if (ds_SVHE324UC_0.rowcount < 1){
		alert(setPopMessage("3040"));
		setMessage("3040");
		return;
