	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	if(ds_SVHE324UC_1.rowcount <1){
		alert(setPopMessage("3040"));
		setMessage("3040");
		return;
	}

	//거래처,품번별 집계
	ds_SVHE324UC_1.Sort("COMPS,PYVND,PSPNO");
	for(var i=0;i<ds_SVHE324UC_1.rowcount;i++){
		if(ds_SVHE324UC_1.GetColumn(i,"COMPS") != ds_SVHE324UC_1.GetColumn(i+1,"COMPS") 
		|| ds_SVHE324UC_1.GetColumn(i,"PYVND") != ds_SVHE324UC_1.GetColumn(i+1,"PYVND")
		|| ds_SVHE324UC_1.GetColumn(i,"PSPNO") != ds_SVHE324UC_1.GetColumn(i+1,"PSPNO")){		
		var t_co = ds_SVHE324UC_1.GetColumn(i,"COMPS");
		var t_py = ds_SVHE324UC_1.GetColumn(i,"PYVND");
		var t_ps = ds_SVHE324UC_1.GetColumn(i,"PSPNO");
		//trace(t_co+t_py+t_ps);
		//trace(ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","BEFSLR"));	
		var nRow1 = ds_SVHE324UC.AddRow();
		ds_SVHE324UC.SetColumn(nRow1,"COMPS",t_co);
		ds_SVHE324UC.SetColumn(nRow1,"PYVND",t_py);
		ds_SVHE324UC.SetColumn(nRow1,"PSPNO",t_ps);
		ds_SVHE324UC.SetColumn(nRow1,"DMKCR",ds_SVHE324UC_1.GetColumn(i,"DMKCR"));
		ds_SVHE324UC.SetColumn(nRow1,"DSECD",ds_SVHE324UC_1.GetColumn(i,"DSECD"));
		ds_SVHE324UC.SetColumn(nRow1,"DSENO",ds_SVHE324UC_1.GetColumn(i,"DSENO"));
		ds_SVHE324UC.SetColumn(nRow1,"DFACD",ds_SVHE324UC_1.GetColumn(i,"DFACD"));
		ds_SVHE324UC.SetColumn(nRow1,"DEFFR",ds_SVHE324UC_1.GetColumn(i,"DEFFR"));
		ds_SVHE324UC.SetColumn(nRow1,"DEFTO",ds_SVHE324UC_1.GetColumn(i,"DEFTO"));
		ds_SVHE324UC.SetColumn(nRow1,"DUMCA",ds_SVHE324UC_1.GetColumn(i,"DUMCA"));
		ds_SVHE324UC.SetColumn(nRow1,"DUMCB",ds_SVHE324UC_1.GetColumn(i,"DUMCB"));
		ds_SVHE324UC.SetColumn(nRow1,"DUMCC",ds_SVHE324UC_1.GetColumn(i,"DUMCC"));
		ds_SVHE324UC.SetColumn(nRow1,"PROGB",ds_SVHE324UC_1.GetColumn(i,"PROGB"));
		ds_SVHE324UC.SetColumn(nRow1,"PLNGB",ds_SVHE324UC_1.GetColumn(i,"PLNGB"));
		ds_SVHE324UC.SetColumn(nRow1,"VENDR",ds_SVHE324UC_1.GetColumn(i,"VENDR"));
		ds_SVHE324UC.SetColumn(nRow1,"FILID",ds_SVHE324UC_1.GetColumn(i,"FILID"));
		ds_SVHE324UC.SetColumn(nRow1,"MAGGB",ds_SVHE324UC_1.GetColumn(i,"MAGGB"));
		ds_SVHE324UC.SetColumn(nRow1,"DTAGB",ds_SVHE324UC_1.GetColumn(i,"DTAGB"));
		ds_SVHE324UC.SetColumn(nRow1,"ITMNO",ds_SVHE324UC_1.GetColumn(i,"ITMNO"));
		ds_SVHE324UC.SetColumn(nRow1,"PNONM",ds_SVHE324UC_1.GetColumn(i,"PNONM"));
		ds_SVHE324UC.SetColumn(nRow1,"JOBGB",ds_SVHE324UC_1.GetColumn(i,"JOBGB"));
		ds_SVHE324UC.SetColumn(nRow1,"ITMCD",ds_SVHE324UC_1.GetColumn(i,"ITMCD"));
		ds_SVHE324UC.SetColumn(nRow1,"FACCD",ds_SVHE324UC_1.GetColumn(i,"FACCD"));
		ds_SVHE324UC.SetColumn(nRow1,"TRIMD",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","TRIMD"));
		ds_SVHE324UC.SetColumn(nRow1,"PBSQY",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","PBSQY"));
		ds_SVHE324UC.SetColumn(nRow1,"PATQY",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","PATQY"));
		ds_SVHE324UC.SetColumn(nRow1,"WBSQY",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","WBSQY"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT00",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT00"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT01",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT01"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT02",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT02"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT03",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT03"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT04",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT04"));
