		ds_SVHE324UC.SetColumn(nRow1,"DAT06",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT06"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT07",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT07"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT08",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT08"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT09",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT09"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT10",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT10"));
		ds_SVHE324UC.SetColumn(nRow1,"DAT11",ds_SVHE324UC_1.CaseSum("COMPS == '"+t_co+"' && PYVND == '"+t_py+"' && PSPNO == '"+t_ps+"'","DAT11"));
