/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setSVHE324UC"){
		if (ds_SVHE324UC_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SVHE324UC_CHECK.GetRowCount();i++){		
				alert(ds_SVHE324UC_CHECK.GetColumn(i,"PYVND") + " , "
					+ ds_SVHE324UC_CHECK.GetColumn(i,"PSPNO") + " = "
					+ ds_SVHE324UC_CHECK.GetColumn(i,"RTMSG"));	
			ds_SVHE324UC_2.AddRow();
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"SPACE",'4');
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"PYVND",ds_SVHE324UC_CHECK.GetColumn(i,"PYVND"));
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"PSPNO",ds_SVHE324UC_CHECK.GetColumn(i,"PSPNO"));
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"DMKCR",ds_SVHE324UC_CHECK.GetColumn(i,"DMKCR"));
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"DSECD",ds_SVHE324UC_CHECK.GetColumn(i,"DSECD"));
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"DSENO",ds_SVHE324UC_CHECK.GetColumn(i,"DSENO"));
			ds_SVHE324UC_2.SetColumn(ds_SVHE324UC_2.row,"DFACD",ds_SVHE324UC_CHECK.GetColumn(i,"DFACD"));
			}
			return;	
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setSVHE324UC"){

		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SVHE324UC.ClearData();
	ds_SVHE324UC_CHECK.ClearData();
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 컬럼 포켓 셋팅
 * 주요변수 : 
 **********************************************************************/ 
function setColumn(cnt)
{
	var returnTxt = "";
	for(i=1;i<=cnt;i++)
	{
		if(i<10)
		{
			if(i == cnt) returnTxt += "Col0" + i;
			else returnTxt += "Col0" + i + ",";
		} else {
			if(i == cnt) returnTxt += "Col" + i;
			else returnTxt += "Col" + i + ",";
		}
	}
	return returnTxt ;
}
