function getSVKE110Q(){

	if(!validationChk()){
		return;
	}

	var orgcomps="";
	if(ComboBoxPlant.Value=="E1"){
		orgcomps ="PE";
	}
	else{
		orgcomps ="PS";
	}
	var sKind		= "getCGMPARMGB";		
	var sMethodName = "service::pscomlib/selectCGMPARMGB.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMGB=ds_CGMPARMGB";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " GCOMPS=" + quote(orgcomps); //회사(PE/PS)
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_CGMPARMGB.rowcount < 1){
		alert(setPopMessage("4033"));
		setMessage("4033");
		return;
	}
	var arrHouse = "('";
	for(i=0; i< ds_CGMPARMGB.rowcount; i++){
		if(i == ds_CGMPARMGB.rowcount-1){
			arrHouse = arrHouse+ds_CGMPARMGB.GetColumn(i,"MAJCD_H") + "')";
		}else{
			arrHouse = arrHouse+ds_CGMPARMGB.GetColumn(i,"MAJCD_H") + "','";
		}
	}

	//RPG SVKE119CP : ds_SVKE110Q_SVK119PF
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " CHKDT=" + quote("PDATE"); //시작일자 PARAM
		sArgument += " CHKCNT=" + quote(13); //리턴수
		sArgument += " STDDT=" + quote(cmb_DUMCB.Value); //시작일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	for(i=0; i<13; i++){
			if(i<11){
				MainGrid.SetCellProp("Head", 14+i, "text", substr(ds_MST090PF.GetColumn(i,"PRCDT"),0,4)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),6,2));
				MainGrid2.SetCellProp("Head", 14+i, "text", substr(ds_MST090PF.GetColumn(i,"PRCDT"),0,4)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),6,2));
			}
			MainGrid4.SetCellProp("Head", 12+i, "text", substr(ds_MST090PF.GetColumn(i,"PRCDT"),0,4)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),6,2));
		}

	var sKind		= "getSVKE110Q";						
	var sMethodName = "service::pssallib/selectSVKE110Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVKE110Q=ds_SVKE110Q ds_SVKE110Q_SVK119PF=ds_SVKE110Q_SVK119PF";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체 (SVK110PF, SVK119PF)
		sArgument += "@DUMCB=" + quote(cmb_DUMCB.Value); //기준일자 (SVK110PF, SVK119PF)
		sArgument += "@JPYYM=" + quote(substr(cmb_DUMCB.Value,0,6)); //기준일자 (SVK110PF, SVK119PF)
		sArgument += "@SEL=" + quote(cmb_SEL.Value); //발행구분
		sArgument += "@HOUSE=" + quote(arrHouse);//창고코드(SVK110PF)
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_PYVND.BKColor="white";		
	cmb_DUMCB.BKColor="white";		
	if(length(trim(st_PYVND.Text)) == 0){
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("2516"));
		setMessage("2516");	
		result = false;
	}else if(cmb_DUMCB.Value==""){
		cmb_DUMCB.BKColor="paleturquoise";
		//alert("[알림]PRODUCTION DATE를 입력하세요.");
		alert(setPopMessage("6073"));//[알림]PRODUCTION DATE를 입력하세요.
		setMessage("6073");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SVKE110Q.ClearData();
	ds_SVKE110Q_SVK119PF.ClearData();

	if(rdoggub.Value==1){
		if(cmb_SEL.Value=""){
			MainGrid.Visible=true;
			MainGrid2.Visible=false;
			MainGrid3.Visible=false;
		}else if(cmb_SEL.Value=1){
			MainGrid.Visible=false;
			MainGrid2.Visible=true;
			MainGrid3.Visible=false;
		}else if(cmb_SEL.Value=2){
			MainGrid.Visible=false;
			MainGrid2.Visible=false;
			MainGrid3.Visible=true;
		}
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	if(rdoggub.Value==1){
		var fName   = "기아 일일부품 소요계획 현황(종합용)";
		var arrSheets  =  Array();  

		arrSheets[0]  = "MainGrid|기아 일일부품 소요계획 현황(종합용)";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}
	if(rdoggub.Value==2){
		var fName   = "기아 일일부품 소요계획 현황(종합용) ERROR LIST";
		var arrSheets  =  Array();  

		arrSheets[0]  = "MainGrid2|기아 일일부품 소요계획 현황(종합용) ERROR LIST";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}
}

