}

function getSvme100(){

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("4674"));  //[알림]업체를 입력하세요.
		setMessage("4674");	
		ed_PYVND.SetFocus();
		return;
	}

	var sKind		= "getSvme100";						
	var sMethodName = "service::pssallib/selectSvme100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_svme100=ds_svme100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //품번
	sArgument += "@PCHK=" + quote(rdoggub.Value); //조회구분
	//sArgument += "@GUBCD=" + quote(gubcd.Value); //구분flag
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setSvme100(){
	for(i=0; i< ds_svme100.GetRowCount(); i++){
		if(ds_svme100.GetRowType(i) == "insert" || ds_svme100.GetRowType(i) == "update"){
			var k_mcrcd = ds_svme100.GetColumn(i,"MCRCD");
			var k_seqcd = ds_svme100.GetColumn(i,"SEQCD");
			//중복차종 서열코드마스타 Check(중복차종서열코드마스타에　등록된　대표차종,서열은　등록不)
			http.Sync = true;
			var sKind		= "getsvz080pf";		
			var sMethodName = "service::pssallib/selectSvme100_Check1.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_svze080=ds_svze080";
			var sArgument   = "";
				sArgument  += " LIB1=" + quote(Lib1);
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " PYVND=" + quote(ed_PYVND.Text); //업체
				sArgument  += " MCRCD=" + quote(k_mcrcd);
				sArgument  += " SEQCD=" + quote(k_seqcd);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_svze080.GetRowCount() > 0){
				alert(setPopMessage("4017"));
				setMessage("4017");
				MainGrid.SetFocus();
				ds_svme100.row =i;
				MainGrid.SetCellPos(4);
				MainGrid.VScrollPos =i;
				return;
			}
		}
	}
	if(ds_svme100.GetRowType(ds_svme100.currow)=="update"){
		if(!ds_svme100_CanRowPosChange()){
			return;
		}
	} 		
	//ds_svme100_CanRowPosChange();
	var sKind		= "setSvme100";						
	var sMethodName = "service::pssallib/svme100Save.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_svme100=ds_svme100:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_svme100_check=ds_svme100_check";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	//sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " USERID=" + quote(userID);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_svme100_check.GetRowCount() > 0){			
		for(var i=0;i<ds_svme100_check.GetRowCount();i++){		
			alert(ds_svme100_check.GetColumn(i,"PYVND") + "," 
			    + ds_svme100_check.GetColumn(i,"PSPNO") + " ="
			    + ds_svme100_check.GetColumn(i,"RTMSG"));	
		}
		//getSvme100();        //error항목이 존재시에는 재조회		
		return;		
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setSvme100"){
		alert(setPopMessage("1001"));
		setMessage("1001");
		//getSvme100();  //성공시 재조회 (다음저장을 위하여) 
	}	
}
