function ds_svme100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SEQCD"){
		if(varNewValue!=""){
			//<BAR CODE 정보마스타 Check (BAR CODE 정보마스타의 ALC코드와 같지 않으면　등록不)
			http.usewaitcursor = false;
			http.Sync = true;
			var sKind		= "getMST200PF";		
			var sMethodName = "service::pscomlib/selectMST200PF.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_MST200PF=ds_MST200PF";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " PSPNO=" + quote(ds_svme100.GetColumn(nRow,"PSPNO")); //품번
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			http.usewaitcursor = true;
			if(ds_MST200PF.GetRowCount() > 0){
				var aclCd=trim(ds_MST200PF.GetColumn(0,"MKALC"));
				if (length(aclCd) >0 && aclCd != varNewValue){
				//if (aclCd != varNewValue){
					alert(setPopMessage("4016"));
					setMessage("4016");
					alert("ACL CODE : "+trim(ds_MST200PF.GetColumn(0,"MKALC")));
					return false;
				}
			}
		}
	}

	if (strColumnID == "EFFFR"){
		if(varNewValue != "" && varNewValue!=99999999){
			if(length(varOldValue)!=0 || varOldValue != varNewValue){
				var tval=getSAL100PF(varNewValue);
				if(tval==0){
					alert(setPopMessage("1504"));  //[[확인] 시작일자를 확인바랍니다.
					setMessage("1504");	
				return false;
				}

			}
			if((length(ds_svme100.GetColumn(nRow,"EFFTO")) > 0 ) && (toNumber(varNewValue) > toNumber(ds_svme100.GetColumn(nRow,"EFFTO")))){
				//	alert("FR날짜확인");  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				setMessage("1503");	
			return false;
			}		
		}
	}
	if (strColumnID == "EFFTO"){
		if(varNewValue != "" && varNewValue!=99999999){
			if(length(varOldValue)!=0 || varOldValue != varNewValue){
				var tval=getSAL100PF(varNewValue);
				if(tval==0){
					alert(setPopMessage("1505"));  //[[확인] 종료일자를 확인바랍니다.
					setMessage("1505");	
				return false;
				}
			}
			if((length(ds_svme100.GetColumn(nRow,"EFFFR")) > 0 ) && (toNumber(ds_svme100.GetColumn(nRow,"EFFFR")) > toNumber(varNewValue))){
				//alert("TO날짜확인");  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				setMessage("1503");	
			return false;
			}				
		}
	}
}

function ds_svme100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "EFFFR"){
		if(length(ds_svme100.GetColumn(nRow,"EFFTO")) < 8)
			ds_svme100.SetColumn(nRow,"EFFTO","99999999");
		}
	}
}
function ds_svme100_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;
	if(ds_svme100.GetRowType(ds_svme100.currow)=="update" ||ds_svme100.GetRowType(ds_svme100.currow)=="insert" ){
		var seqcd=ds_svme100.GetColumn(ds_svme100.currow,"SEQCD"); //서열코드가 null이 아닌경우 등록/수정함.
		var efffr=ds_svme100.GetColumn(ds_svme100.currow,"EFFFR"); //적용일자FR
		var effto=ds_svme100.GetColumn(ds_svme100.currow,"EFFTO"); //적용일자TO
		var sysDt =ds_SYSDATE.GetColumn(0,"DT2");
		//var systm =ds_SYSDATE.GetColumn(0,"TM");
		if(length(seqcd) > 0){ 
			if(length(efffr) == 0){
				ds_svme100.SetColumn(ds_svme100.currow,"EFFFR",sysDt);
			 }
			if(length(effto) == 0){
				ds_svme100.SetColumn(ds_svme100.currow,"EFFTO",99999999);
			 }			 
		}
	}
	return true;
	http.usewaitcursor = true;	
}

function getSAL100PF(makdt)
{
	http.usewaitcursor = false;
	http.Sync=true;
	var sKind		= "getSAL100PF";		
	var sMethodName = "service::pscomlib/selectSAL100.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SAL100=ds_SAL100";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PYVND=" + quote(ed_PYVND.Text);
		sArgument += " MAKDT=" + quote(makdt); //작업일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_SAL100.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
	http.usewaitcursor = true;	
}
