function setExcel()
{
	var fName   = "업체서열차종 마스터";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체서열차종 마스터";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

