function getSvze080(){

	if(!validationChk()){
		return;
	}	
	var sKind		= "getSvze080";						
	var sMethodName = "service::pssallib/selectSvze080.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVZE080=ds_SVZE080";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@MCRCD=" + quote(ed_MCRCD.Text); //대표차종
	sArgument += "@MSECD=" + quote(ed_MSECD.Text); //대표서열
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

	sPspno.Text = trim(ds_SVZE080.GetColumn(0,"PSPNO"));
	sItdsc.Text = trim(ds_SVZE080.GetColumn(0,"ITDSC"));
	sCusnm.Text = trim(ds_SVZE080.GetColumn(0,"CUSNM"));
	sMstsm.Text = trim(ds_SVZE080.GetColumn(0,"CARNM"));
	sCprtn.Text = trim(ds_SVZE080.GetColumn(0,"CPRTN"));

}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setSvze080(){

	var sKind		= "setSvze080";						
	var sMethodName = "service::pssallib/svze080Save.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SVZE080=ds_SVZE080:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVZE080_CHK=ds_SVZE080_CHK";  							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if (ds_SVZE080_CHK.GetRowCount() > 0){
		for(var i=0;i<ds_SVZE080_CHK.GetRowCount();i++){
			alert("차종 : " +ds_SVZE080_CHK.GetColumn(i,"MCRC1") + ", 서열 : " +ds_SVZE080_CHK.GetColumn(i,"MSEC1") + " = " + ds_SVZE080_CHK.GetColumn(i,"RTMSG"));
		}
		getSvze080();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	ed_PYVND.BKColor="white";
	ed_MCRCD.BKColor="white";
	ed_MSECD.BKColor="white";

	var rlt = true;
	var pyvndVal = st_PYVND.Text;
	var mcrcdVal = ed_MCRCD.Text;
	var seqcdVal = ed_MSECD.Text;

	if (pyvndVal == "" || mcrcdVal == "" ||seqcdVal="")
	{
		var chkVal=0;
		var chkNm="";
		if(pyvndVal == ""){
			ed_PYVND.BKColor="paleturquoise";
			if(chkVal==0){
				chkNm="업체";
				chkVal =1;
			}
		}
		if(mcrcdVal == ""){
			ed_MCRCD.BKColor="paleturquoise";
			if(chkVal==0){
				chkNm="대표차종";
				chkVal =1;
			}
			else{
				chkNm=chkNm+", 대표차종";
			}
		}
		if(seqcdVal == ""){
			ed_MSECD.BKColor="paleturquoise";
			if(chkVal==0){
				chkNm="대표서열";
				chkVal =1;
			}
			else{
				chkNm=chkNm+", 대표서열";
			}
		}
		alert(chkNm+"을 입력하세요.");
		//alert(setPopMessage("3048"));
		rlt = false;
	}
	return rlt;
}

