function fn_Input()
{
	if(checkInsertCount("ds_SVZE080")== false) return;
	if(ds_SVZE080.rowcount < 1) fn_Search();  
	ds_SVZE080.AddRow();
	ds_SVZE080.SetColumn(ds_SVZE080.Row, "COMPS", ComboBoxPlant.Value); // default값 설정
	ds_SVZE080.SetColumn(ds_SVZE080.Row, "PYVND", ed_PYVND.Text); // default값 설정
	ds_SVZE080.SetColumn(ds_SVZE080.Row, "MCRCD", ed_MCRCD.Text); // default값 설정
	ds_SVZE080.SetColumn(ds_SVZE080.Row, "MSECD", ed_MSECD.Text); // default값 설정
}

