function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	MainGrid.UserData = MainGrid.Height;
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	//displayDiv.Width = G_ds_design.GetColumn(0,"Width");

	//searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSvzE505(){

	if(!validationChk()){
		return;
	}	
	var sKind		= "getSvzE505";						
	var sMethodName = "service::pssallib/selectSVZE505.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVZE505=ds_SVZE505";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@CARCD=" + quote(ed_CARCD.Text); //차종
	sArgument += "@BDYCD=" + quote(ed_BDYCD.Text); //규격
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSvzE505(){

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}	
	if(ds_SVZE505.GetRowType(ds_SVZE505.currow)=="update" ||ds_SVZE505.GetRowType(ds_SVZE505.currow)=="insert" ){
		if(!ds_SVZE505_CanRowPosChange()){
			return;
		}
	} 			
	var sKind		= "setSvzE505";						
	var sMethodName = "service::pssallib/insertSVZE505.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SVZE505=ds_SVZE505:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVZE505_CHECK=ds_SVZE505_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SCOMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " SPYVND=" + quote(ed_PYVND.Text); //업체
		sArgument += " SCARCD=" + quote(ed_CARCD.Text); //업체
		sArgument += " SBDYCD=" + quote(ed_BDYCD.Text); //업체
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SVZE505_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SVZE505_CHECK.GetRowCount();i++){
			alert("업체 : " +ds_SVZE505_CHECK.GetColumn(i,"PYVND") + ", 차종 : " +ds_SVZE505_CHECK.GetColumn(i,"CARCD") +  ", 규격 : " +ds_SVZE505_CHECK.GetColumn(i,"BDYCD") + " = " + ds_SVZE505_CHECK.GetColumn(i,"RTMSG"));
		}
		getSvzE505();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSvzE505();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pyvndVal = st_PYVND.Text;
	var carcdVal = ed_CARCD.Text;
	var bdycdVal = ed_BDYCD.Text;
		ed_PYVND.BKColor="white";
		ed_CARCD.BKColor="white";
		ed_BDYCD.BKColor="white";

	if (pyvndVal == "")
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("2516"));//[경고] 필수입력항목입니다. (업체번호)
		result = false;
	}
	else if (carcdVal == "")
	{
		ed_CARCD.BKColor="paleturquoise";
		alert(setPopMessage("4675")); //[알림]대표차종을 입력하세요.
		result = false;
	}
	else if (bdycdVal == "")
	{
		ed_BDYCD.BKColor="paleturquoise";
		alert(setPopMessage("4676")); //[알림]대표규격을 입력하세요.
		result = false;
	}		

	return result;
}

