function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_SVZE505")== false) return;
	if(ds_SVZE505.rowcount < 1) fn_Search();  
	ds_SVZE505.AddRow();
	ds_SVZE505.SetColumn(ds_SVZE505.Row, "COMPS1", ComboBoxPlant.Value); // default값 설정
	ds_SVZE505.SetColumn(ds_SVZE505.Row, "PYVND1", ed_PYVND.Text); // default값 설정
	ds_SVZE505.SetColumn(ds_SVZE505.Row, "CARCD1", ed_CARCD.Text); // default값 설정
	ds_SVZE505.SetColumn(ds_SVZE505.Row, "BDYCD1", ed_BDYCD.Text); // default값 설정
}
function fn_Delete()
{
	if(ds_SVZE505.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SVZE505.DeleteRow(ds_SVZE505.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");	
}

