function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}
function getMST050CHK(tval, compCHK){

	http.usewaitcursor = false;
	var sKind		= "getMST050CHK";						
	var sMethodName = "service::pssallib/selectMST050_MRP.do";	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST050=ds_MST050";    	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(compCHK); //사업부
		sArgument += " TFLAG=" + quote(tfalg); //체크구분
		//sArgument += " TVAL=" + quote(obj.Text); 
		sArgument += " TVAL=" + quote(tval); 
	http.usewaitcursor = true;	
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_MST050.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}

function getPYVNDCHK(pyv){

	http.usewaitcursor = false;
	var sKind		= "getPYVNDCHK";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(pyv);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	http.usewaitcursor = true;	

	if(ds_CUSMAS1.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}

