function ds_SVZE505_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var compCHK=ds_SVZE505.GetColumn(ds_SVZE505.currow,"COMPS2");
	var compCAR=ds_SVZE505.GetColumn(ds_SVZE505.currow,"CARCD2");
	var compBDY=ds_SVZE505.GetColumn(ds_SVZE505.currow,"BDYCD2");
	if( strColumnID == "COMPS2" ){
		if(varNewValue != "" ){	
			if(company =="E1"){
				if(varNewValue != "E1"){
					alert(setPopMessage("4672"));//[알림]사업부는 E1만 입력가능합니다.
					MainGrid.SetFocus();
					return false;
				}
/*					
				if(length(compCAR)!=0){
					tfalg="CAR";
					var tval=getMST050CHK(compCAR, varNewValue);
					if(tval==0){
						alert(setPopMessage("4665"));//[확인]차종이 존재하지 않습니다.
						MainGrid.SetCellPos(2);
						MainGrid.SetFocus();
						MainGrid.ShowEditor();
						MainGrid.SetEditText(" ");
					}
				}
				if(length(compBDY)!=0){
					tfalg="BDY";
					var tval=getMST050CHK(compBDY, varNewValue);
					if(tval==0){
						alert(setPopMessage("4666"));//[확인]규격이 존재하지 않습니다.
						//해당 cell이동하여 focus를 줌.
						MainGrid.SetCellPos(3);
						MainGrid.SetFocus();
						//MainGrid.ShowEditor();
						MainGrid.SetEditSelText("t");
					}
				}
*/								
			}else{
				if(varNewValue != "C1" && varNewValue != "H1" && varNewValue != "S1"){
					alert(setPopMessage("4673"));//[알림]사업부는 C1, H1, S1만 입력가능합니다.
					MainGrid.SetFocus();
					return false;
				}				
			}
		}
	}
	if( strColumnID == "PYVND2" ){
		if(varNewValue != ""){
			var tval=getPYVNDCHK(varNewValue);
			if(tval==0){
				alert(setPopMessage("4671"));//[확인] 업체 확인바랍니다.
				MainGrid.SetFocus();
				return false;
			}
		}
	}

	if( strColumnID == "CARCD2" ){
		if(varNewValue != "" && length(compCHK) !=0){
			tfalg="CAR";
			var tval=getMST050CHK(varNewValue, compCHK);
			if(tval==0){
				alert(setPopMessage("4665"));//[확인]차종이 존재하지 않습니다.
				MainGrid.SetFocus();
				return false;
			}
		}
	}
	if( strColumnID == "BDYCD2" ){
		if(varNewValue != "" && length(compCHK) !=0){
			tfalg="BDY";
			var tval=getMST050CHK(varNewValue, compCHK);
			if(tval==0){
				alert(setPopMessage("4666"));//[확인]규격이 존재하지 않습니다.
				MainGrid.SetFocus();
				return false;
			}					
		}
	}

}
function ds_SVZE505_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var compCHK=ds_SVZE505.GetColumn(ds_SVZE505.currow,"COMPS2");
	var compCAR=ds_SVZE505.GetColumn(ds_SVZE505.currow,"CARCD2");
	var compBDY=ds_SVZE505.GetColumn(ds_SVZE505.currow,"BDYCD2");

	if (strColumnID == "COMPS2"){
		if(varNewValue!=""){
			if(length(compCAR)!=0){
				tfalg="CAR";
				var tval=getMST050CHK(compCAR, varNewValue);
				if(tval==0){
					alert(setPopMessage("4665"));//[확인]차종이 존재하지 않습니다.
					ds_SVZE505.SetColumn(nRow,"CARCD2","");
					MainGrid.SetCellPos(2);
					MainGrid.SetFocus();
					MainGrid.ShowEditor();
					MainGrid.SetEditText("");
				}
				if(length(compBDY)!=0){
					tfalg="BDY";
					var tval=getMST050CHK(compBDY, varNewValue);
					if(tval==0){
					ds_SVZE505.SetColumn(nRow,"BDYCD2","");
						alert(setPopMessage("4666"));//[확인]규격이 존재하지 않습니다.
						//해당 cell이동하여 focus를 줌.
						MainGrid.SetCellPos(3);
						MainGrid.SetFocus();
						//MainGrid.ShowEditor();
						MainGrid.SetEditText("");
					}
				}				
			}
		}			
	}
}
function ds_SVZE505_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_SVZE505.GetRowType(ds_SVZE505.currow)=="update" ||ds_SVZE505.GetRowType(ds_SVZE505.currow)=="insert" ){
		var comps2=ds_SVZE505.GetColumn(ds_SVZE505.currow,"COMPS2");
		var pyvnd2=ds_SVZE505.GetColumn(ds_SVZE505.currow,"PYVND2");
		var carcd2=ds_SVZE505.GetColumn(ds_SVZE505.currow,"CARCD2");
		var bdycd2=ds_SVZE505.GetColumn(ds_SVZE505.currow,"BDYCD2");

		if(length(comps2)!=0){
			if(pyvnd2==0 || length(pyvnd2)==0){
				alert(setPopMessage("4674"));//[알림]업체를 입력하세요.
				//MainGrid.AutoEnter=true;
				result=false;
			}
			else if(carcd2==0 || length(carcd2)==0){
				alert(setPopMessage("4675")); //[알림]차종을 입력하세요.
				//MainGrid.AutoEnter=true;
				result=false;
			}
			else if(bdycd2==0 || length(bdycd2)==0){
				alert(setPopMessage("4676")); //[알림]규격을 입력하세요.
				//MainGrid.AutoEnter=true;
				result=false;
			}
		}
	return result;		
	}
}
