/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	if(rdoggub.Value==1){
		var fName   = "업체품번별제품조회(구MRP)";
		var arrSheets  =  Array();  

		arrSheets[0]  = "MainGrid|업체품번별제품조회(구MRP)";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}
	else{
		var fName   = "품번별제품마스타조회(구MRP)";
		var arrSheets  =  Array();  

		arrSheets[0]  = "MainGrid2|품번별제품마스타조회(구MRP)";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}
}

