function getSzze101(){

	if(!validationChk()){
		return;
	}
	var slipTo = "";
	var slipTo = ed_SNOTO.Text;
	if(slipTo==""){
		slipTo="9999999999";
	}
	var sKind		= "getSzze101";						
	var sMethodName = "service::pssallib/selectSzze101.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_szze101_rep=ds_SZZE101";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " SNOFR=" + quote(ed_SNOFR.Text); //SLIP NO FROM
	sArgument += " SNOTO=" + quote(slipTo); //SLIP NO TO
	sArgument += " TRNDTS=" + quote(cmb_TRNDTS.Value); //TRANSFER DATE
	//var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_szze101_rep.GetRowCount() <=0){
		alert(setPopMessage("1003"));
		setMessage("1003");
		return;
	}
	dialog("pssallib::SZZE101CP_rep.xml", "",300,300,"TitleBar=true");
/*	
	if(ds_SZZE101.GetRowCount() <=0){
		alert(setPopMessage("1003"));
		setMessage("1003");
		return;
	}


	if (str == 'T'){  //정상조회
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
*/	
}


