/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;

	if(cmb_TRNDTS.Value =="" || cmb_TRNDTS.Value < 20030101 || cmb_TRNDTS.Value > 99991231){
		alert("전표일자 입력범위는 20030101~99991231입니다.");
		cmb_TRNDTS.Value="";
		//alert(setPopMessage("1011"));
		//setMessage("1011");
	result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_szze101.DeleteAll();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "TRANSFER 인쇄";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|TRANSFER 인쇄";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}
