function setExcel()
{
	var fName   = "TRANSFER 인쇄";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|TRANSFER 인쇄";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

