function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function Div0_ed_SLPNO_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar != 13){
		return;
	}
	if(length(trim(obj.Text)) < 1){
		return;
	}
	Div1.ed_SLPNO_M.Text = "";
	Plag_77 = "OFF"; Plag_78 = "OFF"; Plag_79 = "OFF"; Plag_88 = "OFF"; 
	//기존 존재유무check-------------------------------------------------------------------------------------
	//생관SCRAP Ticket에 존재
	var sKind		= "getSZZE205_205PF";						
	var sMethodName = "service::pssallib/selectSZZE205_205PF.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SZZE205_205PF=ds_SZZE205_205PF";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " SLPNO=" + quote(trim(obj.Text));
	HTTP.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	HTTP.Sync = false;
	if(ds_SZZE205_205PF.rowcount > 0){
		if(ds_SZZE205_205PF.GetColumn(0,"CGMCDA") == " "){
			alert(setPopMessage("4748"));  //생관 SCRAP TICKET 이 CIGMA 갱신이　처리되지　않았읍니다
			setMessage("4748");
			return;
		}else{
			Plag_77 = "ON";
			Div1.ed_HOUSE.Value = ds_SZZE205_205PF.GetColumn(0,"HOUSEA");
			Div1.ed_REASN.value = ds_SZZE205_205PF.GetColumn(0,"REASNA");
			Div1.ca_TRNDT.Value = ds_SZZE205_205PF.GetColumn(0,"TRNDTA");
			Div1.ed_ISSDP.value = ds_SZZE205_205PF.GetColumn(0,"RTNDPA"); // ISSDPA <-> RTNDPA
			Div1.ed_RTNDP.Value = ds_SZZE205_205PF.GetColumn(0,"ISSDPA"); // ISSDPA <-> RTNDPA
		}
	}else{
		//slip no 1~3자리 업체 check
		var p_vender = substr(trim(obj.Text),0,3);
		http.Sync 	= true;
		var sKind		= "getVENNAM";		
		var sMethodName = "service::pscomlib/selectVENNAM.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_VENNAM=ds_VENNAM";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " VNDNR=" + quote(p_vender);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_VENNAM.GetRowCount() != 1){
			alert(setPopMessage("4749")); //SLIP NO 1~3 자리가　업체코드가　아님니다 
			setMessage("4749");
			return;
		}
		Div1.ed_SLPNO_M.Text = obj.Text;
		var sKind		= "getSZZE205_200PF";						
		var sMethodName = "service::pssallib/selectSZZE205_200PF.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_SZZE205_200PF=ds_SZZE205_200PF";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " SLPNO=" + quote(trim(obj.Text));
		HTTP.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		HTTP.Sync = false;
		if(ds_SZZE205_200PF.rowcount > 0){
			if(length(trim(ds_SZZE205_200PF.GetColumn(0,"BSLPNO"))) > 0){
				Plag_88 = "ON";
			}
			if(ds_SZZE205_200PF.GetColumn(0,"UPTCDC") == "Y"){
				alert(setPopMessage("4710"));  //이미 CIGMA 에　갱신된 SLIP NO 입니다  
				setMessage("4710");
				return;
			}else{
				Plag_78 = "ON";
				Div1.ed_HOUSE.Value = ds_SZZE205_200PF.GetColumn(0,"HOUSEC");
				Div1.ed_REASN.value = ds_SZZE205_200PF.GetColumn(0,"REASNC");
				Div1.ca_TRNDT.Value = ds_SZZE205_200PF.GetColumn(0,"TRNDTC");
				Div1.ed_ISSDP.value = ds_SZZE205_200PF.GetColumn(0,"ISSDPC");
				Div1.ed_RTNDP.Value = ds_SZZE205_200PF.GetColumn(0,"RTNDPC");
			}
		}
	}

	Div1.Visible = true;

	if(Plag_77=="ON"){
		Div1.st_slp1_m.Visible = true;
		Div1.ed_SLPNO_M.Visible = true;
		Div1.st_slp2_m.Visible = true;
		Div1.ed_SLPNO_M.SetFocus();
	}else{
		Div1.st_slp1_m.Visible = false;
		Div1.ed_SLPNO_M.Visible = false;
		Div1.st_slp2_m.Visible = false;
		Div1.ed_HOUSE.SetFocus();
	}
	Div0.Enable = false;

	/*
	if(Plag_88=="ON"){
		MainGrid.SetCellProp("body", 1, "edit", "none");
		MainGrid.SetCellProp("body", 2, "edit", "none");
	}
	*/
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSZZE205(){
	if(length(trim(Div0.ed_SLPNO.Text)) < 1){
		alert("SLIP NO를 확인바랍니다.");
		Div0.ed_SLPNO.SetFocus();
		return;
	}
	if(length(trim(Div1.ed_HOUSE.Text)) < 1){
		alert("HOUSE를 확인바랍니다.");
		Div1.ed_HOUSE.SetFocus();
		return;
	}
	if(length(trim(Div1.ed_REASN.Text)) < 1){
		alert("REASN CODE를 확인바랍니다.");
		Div1.ed_REASN.SetFocus();
		return;
	}
	if(length(trim(Div1.ca_TRNDT.Value)) != 8){
		alert("전표일자를 확인바랍니다.");
		Div1.ca_TRNDT.SetFocus();
		return;
	}
	if(!(Div1.ca_INOUT.Value == "1" || Div1.ca_INOUT.Value == "2" || Div1.ca_INOUT.Value == "3")){
		alert("관리부서 확인바랍니다.");
		Div1.ca_INOUT.SetFocus();
		return;
	}
	ds_SZZE205.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	for (var j=0; j<ds_SZZE205.rowcount;j++){
		if(length(trim(ds_SZZE205.GetColumn(j,"PRTNO"))) > 0){
			if(ds_SZZE205.GetColumn(j,"TRQTY") == null){
				alert("QUANTITY(수량)을 확인바랍니다.");
				ds_SZZE205.FireEvent=true;  
				ds_SZZE205.Row =j;
				MainGrid.SetCellPos(3);
				return;
			}
			ds_SZZE205.SetColumn(j, "STKUP", "Y"); //재고갱신
			if(Plag_77=="ON") ds_SZZE205.SetColumn(j, "BSLPNO", Div0.ed_SLPNO.Text); //생관 scrip ticket 전표번호
			ds_SZZE205.SetColumn(j, "CFMCD", "Y"); //확인　유무
			ds_SZZE205.SetColumn(j, "CFMDT", nowDate); //확인　일자
			ds_SZZE205.SetColumn(j, "CFMTM", left(time(),6)); //확인　시간
			ds_SZZE205.SetColumn(j, "CFMMN", userID); //확인처리자
			ds_SZZE205.SetColumn(j, "CFMDP", "DIONE"); //확인단말기
			ds_SZZE205.SetColumn(j, "UPTCD", ""); //확인　유무 cigma
			ds_SZZE205.SetColumn(j, "UPTDT", "0"); //확인　일자 cigma
			ds_SZZE205.SetColumn(j, "UPTTM", "0"); //확인　시간 cigma
			ds_SZZE205.SetColumn(j, "UPTMN", ""); //확인처리자 cigma
			ds_SZZE205.SetColumn(j, "UPTDP", ""); //확인단말기 cigma
		}
	}
	ds_SZZE205.FireEvent=true;  
	var update205;
	if(trim(Div0.ed_SLPNO.Text) != trim(Div1.ed_SLPNO_M.Text)){
		update205 = "Y";
	}else{
		update205 = "N";
	}
 	http.Sync = true;
	var sKind		= "SAVE_SZZE205";						
	var sMethodName = "service::pssallib/saveSZZE205.do";
	var sInDataSet  = "ds_SZZE205=ds_SZZE205:U";
	var sOutDataSet = "ds_SZZE205_CHECK=ds_SZZE205_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " DCOMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " DSLPNO=" + quote(Div1.ed_SLPNO_M.Text);
	sArgument += " GUBUN=" + quote(update205);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

