	Div0.ed_SLPNO.Value ="";
	Div1.ed_HOUSE.Value = "";
	Div1.ed_SLPNO_M.Value = "";
	Div1.ed_REASN.value = "";
	Div1.ca_TRNDT.Value = "";
	Div1.ed_ISSDP.value = "";
	Div1.ed_RTNDP.Value = "";
	Div0.ed_SLPNO.SetFocus();
}
