function ds_SZZE205_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if (strColumnID == "PRTNO"){
		//품번Check
		if(length(trim(varNewValue)) < 1) {
			alert("품번 확인바랍니다.");
			return false;
		}
		http.Sync 	= true;
		var sKind		= "getPrtno";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BM008PR.GetRowCount() < 1){
			http.usewaitcursor = true;
			alert("품번 확인바랍니다.");
			return false;
		}
		//Inventory Master Check
		http.Sync 	= true;
		var sKind		= "getHM000PR";		
		var sMethodName = "service::pscomlib/selectHM000PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_HM000PR=ds_HM000PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);
			sArgument  += " HOUSE=" + quote(Div1.ed_HOUSE.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_HM000PR.GetRowCount() < 1){
			http.usewaitcursor = true;
			alert("Inventory Master에 미등록된 품번입니다.");
			return false;
		}
		//중복Check
		if(ds_SZZE205.FindRow("PRTNO", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}
		//구매관리팀일경우에는 dppsim업체와 비교
		if(ca_INOUT.Value == "2"){
			var sKind		= "getBM400PR";	
			var sMethodName = "service::pscomlib/selectBM400PR.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_BM400PR=ds_BM400PR";
			var sArgument   = "";
				sArgument  += " LIB1=" + quote(Lib1);
				sArgument  += " DPPRTN=" + quote(varNewValue);
			http.Sync 	= true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync 	= false;
			if(ds_BM400PR.rowcount > 0){
				if(!(
				((trim(ds_BM400PR.GetColumn(0,"DPG1S1")) == substr(trim(Div0.ed_SLPNO.Text),0,3))) ||
				((trim(ds_BM400PR.GetColumn(0,"DPG1S2")) == substr(trim(Div0.ed_SLPNO.Text),0,3))) ||
				((trim(ds_BM400PR.GetColumn(0,"DPG1S3")) == substr(trim(Div0.ed_SLPNO.Text),0,3)))
				)){
					alert(setPopMessage("4750"));  //[경고] DPPSIM 에　등록된　업체와 SLIP NO 의　업체코드가 같지 않습니다.
					setMessage("4750");	
					return false;
				}
			}else{
				alert(setPopMessage("4750"));  //[경고] DPPSIM 에　등록된　업체와 SLIP NO 의　업체코드가 같지 않습니다.
				setMessage("4750");	
				return false;
			}
		}
	}
	if (strColumnID == "TRQTY"){
		if(length(trim(varNewValue)) < 1 || toNumber(varNewValue) <= 0){
			alert("수량을 확인바랍니다.");
			return false;
		}
	}
	if (strColumnID == "MOVCD"){
		if(varNewValue != "Y" && varNewValue != ""){
			alert("선택은 'Y', 선택하지　않을경우　공백으로　두시오.");
			return false;
		}
	}
	http.usewaitcursor = true;
}

function ds_SZZE205_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PRTNO"){
		obj.SetColumn(nRow, "ITDSC", ds_BM008PR.GetColumn(0,"ITDSC")); // 품명 설정
		obj.SetColumn(nRow, "UNMSR", ds_BM008PR.GetColumn(0,"UNMSR")); // 단위 설정
		obj.SetColumn(nRow, "CONNO", "0");
		var dpg1s123 = trim(ds_BM400PR.GetColumn(0,"DPG1S1")) + " " + trim(ds_BM400PR.GetColumn(0,"DPG1S2"))+ " " + ds_BM400PR.GetColumn(0,"DPG1S3");
		obj.SetColumn(nRow, "DPG1",dpg1s123);
	}
	http.usewaitcursor = true;
}

