function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1003")); setMessage("1003");
		}
	}
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}

function ed_HOUSE_OnChanged(obj,strText)
{
	//house check -----------------------------------------------------------------------
	var house_1,house_2; 
	var arrHouseT;
	var nIndex;
	http.Sync 	= true;
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO=" + quote("HAC");  //사내창고
		sArgument   += " LIB1=" + quote(Lib1);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	if(ds_SC008PR.rowcount > 0){
		arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
		nIndex = IndexOf(arrHouseT,obj.Text);
		if(nIndex < 0){ 
			house_1=false;
		}else{
			house_1=true;
		}
	}else{
		house_1=false;
	}
	if(house_1 == false){  //rpg source에서 240행을 주석처리하지 않은 것 같음 즉 무상창고'0'은 무조건 안됨
		alert(setPopMessage("4751"));  //SYSTEM CONTROL FILE 에　창고가　없읍니다
		setMessage("4751");
		obj.Text = "";
		obj.SetFocus();
		return;
	}
	http.Sync 	= true;
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO=" + quote("HAP");  //무상창고
		sArgument   += " LIB1=" + quote(Lib1);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	if(ds_SC008PR.rowcount > 0){
		arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
		nIndex = IndexOf(arrHouseT,obj.Text);
		if(nIndex < 0){ 
			house_2=false;
		}else{
			house_2=true;
		}
	}else{
		house_2=false;
	}
	if(house_1 == false && house_2 == false){
		alert(setPopMessage("4751"));  //SYSTEM CONTROL FILE 에　창고가　없읍니다
		setMessage("4751");
		obj.Text = "";
		obj.SetFocus();
		return;
	}
	if(house_1 == false && house_2 == true){
		Plag_79 = "ON";
	}
}

function Div1_ed_SLPNO_M_OnChanged(obj,strText)
{
	if(length(trim(obj.Text)) < 1){
		alert(setPopMessage("4711"));  // [확인]SLIP NO. 확인이 안되었습니다.
		setMessage("4711");
		return;
	}

	if(Plag_77=="ON"){ //생관scrip ticket에 등록된 경우
		Plag_78 = "OFF";
		var sKind		= "getSZZE205_200PF";						
		var sMethodName = "service::pssallib/selectSZZE205_200PF.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_SZZE205_200PF=ds_SZZE205_200PF";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " SLPNO=" + quote(trim(obj.Text));
		HTTP.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		HTTP.Sync = false;
		if(ds_SZZE205_200PF.rowcount > 0){
			alert(setPopMessage("4752"));  //이미　확인완료된 SLIP NO. 입니다
			setMessage("4752");
			obj.Text="";
			obj.SetFocus();
			return;
		}
	}else{ //생관scrip ticket에 등록되지 않은 경우
		//slip no 1~3자리 업체 check
		var p_vender = substr(trim(obj.Text),0,3);
		http.Sync 	= true;
		var sKind		= "getVENNAM";		
		var sMethodName = "service::pscomlib/selectVENNAM.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_VENNAM=ds_VENNAM";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " VNDNR=" + quote(p_vender);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_VENNAM.GetRowCount() != 1){
			alert(setPopMessage("4749")); //SLIP NO 1~3 자리가　업체코드가　아님니다 
			setMessage("4749");
			obj.Text="";
			obj.SetFocus();
			return;
		}

		var sKind		= "getSZZE205_200PF";						
		var sMethodName = "service::pssallib/selectSZZE205_200PF.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_SZZE205_200PF=ds_SZZE205_200PF";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " SLPNO=" + quote(trim(obj.Text));
		HTTP.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		HTTP.Sync = false;
		if(ds_SZZE205_200PF.rowcount > 0){
			if(ds_SZZE205_200PF.GetColumn(0,"UPTCDC") == "Y"){
				alert(setPopMessage("4710"));  //이미 CIGMA 에　갱신된 SLIP NO 입니다  
				setMessage("4710");
				obj.Text="";
				obj.SetFocus();
				return;
			}
		}
	}
}

function ed_REASN_OnChanged(obj,strText)
