function ed_DP_OnChanged(obj,strText)
{
	//ds_SZZE205.ClearData();
	if(strText.Value = "0" || length(strText.Value) < 1){
		return;
	}
	http.usewaitcursor = false;  //transaction호출후 버튼이벤트가 발생되지 않아서...
	http.Sync 	= true;
	var sKind		= "getMST652LF";		
	var sMethodName = "service::pscomlib/selectMST652LF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST652LF=ds_MST652LF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument  += " DPCDCM=" + quote(strText);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if(ds_MST652LF.rowcount < 1){
		alert("부서코드를 확인바랍니다.");
		http.usewaitcursor = true; 
		obj.Text = "";
		obj.SetFocus();
		return;
	}
	http.usewaitcursor = true; 
}

function Div1_common_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar != 13){
		return;
	}
	if(length(trim(Div0.ed_SLPNO.Text)) < 1){
		alert(setPopMessage("4754"));  // SLIP NO. 를　입력하시오 
		setMessage("4754");
		return;
	}
	if(Plag_77=="ON" && length(trim(Div1.ed_SLPNO_M.Text)) < 1){
		alert(setPopMessage("4754"));  // SLIP NO. 를　입력하시오 
		setMessage("4754");
		Div1.ed_SLPNO_M.SetFocus();
		return;
	}
	if(!(ca_INOUT.Value == "1" || ca_INOUT.Value == "2" || ca_INOUT.Value == "3")){
		alert("관리부서 확인바랍니다.");
		Div1.ca_INOUT.SetFocus();
		return;
	}
	// HOUSE code check----------------------------------------------------------------------------
	if(length(Div1.ed_HOUSE.Text) != 1){
		alert(setPopMessage("1513"));  //  창고 입력하시오 
		setMessage("1513");
		Div1.ed_HOUSE.SetFocus();
		return ;
	}
	//구매관리만 '0'창고를 허용-------------------------------------------------------------------
	if(ca_INOUT.Value != "2" && Div1.ed_HOUSE.Text == "0"){
		alert(setPopMessage("4755"));  //  "1"창고만　입력하시오 
		setMessage("4755");
		Div1.ed_HOUSE.SetFocus();
		return ;
	}

	// REASN code check----------------------------------------------------------------------------
	if(Div1.ed_REASN.Text != "21" && Div1.ed_REASN.Text != "22" && Div1.ed_REASN.Text != "28" && Div1.ed_REASN.Text != "29"){
		alert(setPopMessage("4753"));  //  REASON CODE 를　확인하시오 
		setMessage("4753");
		Div1.ed_REASN.SetFocus();
		return ;
	}

	//  전표일자 check----------------------------------------------------------------------------
	if(length(Div1.ca_TRNDT.Value) != 8 || Div1.ca_TRNDT.Value < 19990101){
		alert(setPopMessage("4756"));  //  TRANSFERED DATE 를　확인하시오 
		setMessage("4756");
		Div1.ca_TRNDT.SetFocus();
		return ;
	}

	//dept no Check---------------------------------------------------------------------------------
	if(length(trim(Div1.ed_ISSDP.Value)) < 1) Div1.ed_ISSDP.Value = "0";
	if(length(trim(Div1.ed_RTNDP.Value)) < 1) Div1.ed_RTNDP.Value = "0";
	if(	(trim(Div1.ed_ISSDP.Value)=="0" && trim(Div1.ed_RTNDP.Value)=="0") || (trim(Div1.ed_ISSDP.Value)!="0" && trim(Div1.ed_RTNDP.Value)!="0") ){
		alert(setPopMessage("4757"));  //  CHARGE TO 와 CANCELLED FROM 중 하나만 입력되어야 합니다.
		setMessage("4757");
		Div1.ed_ISSDP.SetFocus();
		return ;
	}

	MainGrid.Visible = true;
	ds_SZZE205.ClearData();
	ds_SZZE205.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅	
	/*
	77:생관scrip ticket존재 on
	78:scrip ticket존재 on
	79:무상창고일때 on
	88:scrip ticket존재하면서 이동전표가 없을경우 on
	if(Plag_77=="ON" && Plag_78=="OFF") Plag_80 = "ON";
	if(Plag_79=="OFF") Plag_81 = "ON";
	if(Plag_77=="ON") Plag_83 = "ON";
	if(Plag_88=="ON") Plag_80 = "ON";
	*/
	if(Plag_77=="ON" && Plag_78=="OFF"){
		for (var i=0; i<ds_SZZE205_205PF.rowcount; i++){
			ds_SZZE205.AddRow(i);
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "PRSDP", ca_INOUT.Value);
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "SLPNO", Div1.ed_SLPNO_M.Text);
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "HOUSE", Div1.ed_HOUSE.Text);
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "TRNDT", Div1.ca_TRNDT.Value);
			if(length(Div1.ed_ISSDP.Text) > 0){
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "ISSDP", Div1.ed_ISSDP.Text);
			}else{
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "ISSDP", "0");
			}
			if(length(Div1.ed_RTNDP.Text) > 0){
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "RTNDP", Div1.ed_RTNDP.Text);
			}else{
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "RTNDP", "0");
			}
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "REASN", Div1.ed_REASN.Text);

			ds_SZZE205.SetColumn(ds_SZZE205.Row, "PRTNO", ds_SZZE205_205PF.GetColumn(i,"PRTNOA"));
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "TRQTY", ds_SZZE205_205PF.GetColumn(i,"TRQTYA"));
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "UNMSR", ds_SZZE205_205PF.GetColumn(i,"UNMSRA"));
			ds_SZZE205.SetColumn(ds_SZZE205.Row, "ITDSC", ds_SZZE205_205PF.GetColumn(i,"ITDSC"));

			if(Div1.ed_ISSDP.Value == ""){
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "QTYCD", "1");
			}else{
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "QTYCD", "2");
			}

			var sKind		= "getBM400PR";		
			var sMethodName = "service::pscomlib/selectBM400PR.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_BM400PR=ds_BM400PR";
			var sArgument   = "";
				sArgument  += " LIB1=" + quote(Lib1);
				sArgument  += " DPPRTN=" + quote(ds_SZZE205_205PF.GetColumn(i,"PRTNOA"));
			http.Sync 	= true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync 	= false;
			if(ds_BM400PR.rowcount > 0){
				var dpg1s123 = trim(ds_BM400PR.GetColumn(0,"DPG1S1")) + " " + trim(ds_BM400PR.GetColumn(0,"DPG1S2"))+ " " + ds_BM400PR.GetColumn(0,"DPG1S3");
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "DPG1",dpg1s123);
			}
		}
	}
	if(Plag_77=="OFF" && Plag_78=="ON"){
		if(ds_SZZE205_200PF.rowcount>0){
			for (var i=0; i<ds_SZZE205_200PF.rowcount; i++){
				ds_SZZE205.AddRow(i);
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "PRSDP", ca_INOUT.Value);
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "SLPNO", Div1.ed_SLPNO_M.Text);
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "HOUSE", Div1.ed_HOUSE.Text);
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "TRNDT", Div1.ca_TRNDT.Value);
				if(length(Div1.ed_ISSDP.Text) > 0){
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "ISSDP", Div1.ed_ISSDP.Text);
				}else{
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "ISSDP", "0");
				}
				if(length(Div1.ed_RTNDP.Text) > 0){
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "RTNDP", Div1.ed_RTNDP.Text);
				}else{
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "RTNDP", "0");
				}
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "REASN", Div1.ed_REASN.Text);

				ds_SZZE205.SetColumn(ds_SZZE205.Row, "PRTNO", ds_SZZE205_200PF.GetColumn(i,"PRTNOC"));
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "TRQTY", ds_SZZE205_200PF.GetColumn(i,"TRQTYC"));
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "UNMSR", ds_SZZE205_200PF.GetColumn(i,"UNMSRC"));
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "CONNO", ds_SZZE205_200PF.GetColumn(i,"CONNOC"));
				ds_SZZE205.SetColumn(ds_SZZE205.Row, "ITDSC", ds_SZZE205_205PF.GetColumn(i,"ITDSC"));
				if(Div1.ed_ISSDP.Value == ""){
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "QTYCD", "1");
				}else{
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "QTYCD", "2");
				}
				var sKind		= "getBM400PR";	
				var sMethodName = "service::pscomlib/selectBM400PR.do"; 
				var sInDataSet  = "";
				var sOutDataSet = "ds_BM400PR=ds_BM400PR";
				var sArgument   = "";
					sArgument  += " LIB1=" + quote(Lib1);
					sArgument  += " DPPRTN=" + quote(ds_SZZE205_205PF.GetColumn(i,"PRTNOC"));
				http.Sync 	= true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync 	= false;
				if(ds_BM400PR.rowcount > 0){
					var dpg1s123 = trim(ds_BM400PR.GetColumn(0,"DPG1S1")) + " " + trim(ds_BM400PR.GetColumn(0,"DPG1S2"))+ " " + ds_BM400PR.GetColumn(0,"DPG1S3");
					ds_SZZE205.SetColumn(ds_SZZE205.Row, "DPG1",dpg1s123);
				}
			}
		}
	}
	if(Plag_77=="OFF" && Plag_78=="OFF"){ //신규입력
		for (var i=0; i<15; i++){
