		MainGrid.SetCellProp("body", 3, "bkcolor", "MENU"); //수량

	}else{
		MainGrid.SetCellProp("body", 1, "edit", "regexprupper"); //품번
		MainGrid.SetCellProp("body", 3, "edit", "integer"); //수량
		MainGrid.SetCellProp("body", 1, "bkcolor", "default"); //품번
		MainGrid.SetCellProp("body", 3, "bkcolor", "default"); //수량
	}
	if(Plag_77=="ON" || Plag_88=="ON" || Plag_79=="OFF"){  //무상창고일경우 edit를 활성화
		MainGrid.SetCellProp("body", 5, "edit", "none"); //conno
		MainGrid.SetCellProp("body", 5, "bkcolor", "MENU"); 
	}else{
		MainGrid.SetCellProp("body", 5, "edit", "normal"); //conno
		MainGrid.SetCellProp("body", 5, "bkcolor", "default"); 
	}
	if(Plag_77=="OFF"){ 
		MainGrid.SetCellProp("body", 6, "edit", "none"); //이동부서
		MainGrid.SetCellProp("body", 6, "bkcolor", "MENU"); 
	}else{
		MainGrid.SetCellProp("body", 6, "edit", "upperchar"); //이동부서
		MainGrid.SetCellProp("body", 6, "bkcolor", "default"); 
	}

	if(Plag_77=="ON" && Plag_78=="OFF"){
		MainGrid.SetFocus();
		ds_SZZE205.rowposition = 0;
		MainGrid.SetCellPos(6);
	}else{
		MainGrid.SetFocus();
		ds_SZZE205.rowposition = 0;
		MainGrid.SetCellPos(1);
	}
	ds_SZZE205.FireEvent=true;
	Div1.Enable = false;

}
