function getSJTZ230(){

	if(!validationChk()){
		return;
	}
	if(length(ed_SHPTO.Text)==0){
		ed_SHPTO.Text="0";
	}

	var sKind		= "getSJTZ230";						
	var sMethodName = "service::sample/selectSJTZ230.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SJTZ230=ds_SJTZ230";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@SHPTO=" + quote(ed_SHPTO.Text); //SHPTO
	sArgument += "@MAKYM=" + quote(substr(cmb_MKYM.Value,0,6)); //입력년월
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSJTZ230(){
	//OR=|| AND=&&
	if(ds_SJTZ230.GetRowType(ds_SJTZ230.currow)=="update" || ds_SJTZ230.GetRowType(ds_SJTZ230.currow)=="insert" ){
		if(!ds_SJTZ230_CanRowPosChange()){
			return;
		}
	} 

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}	
	if(cmb_MKYM.Value == ""){
		alert(setPopMessage("4032"));  //[확인] 입력년월을 확인바랍니다.
		setMessage("4032");		
		cmb_MKYM.SetFocus();
		return;
	}

	var stlwh="";
	var tchk=getHOUSE();
	if(tchk==0){
		alert(setPopMessage("4033"));
		setMessage("4033");		
		//alert("등록된 창고가 없습니다. 업체를 확인해주세요.");
		return false;
	}
	stlwh=ds_EM035PR.GetColumn(0,"STLWH");
	var sKind		= "setSJTZ230";						
	var sMethodName = "service::sample/saveSJTZ230.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SJTZ230=ds_SJTZ230:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SJTZ230_CHECK=ds_SJTZ230_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " STLWH=" + quote(stlwh);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SJTZ230_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SJTZ230_CHECK.GetRowCount();i++){
			alert("업체품번 : " +ds_SJTZ230_CHECK.GetColumn(i,"MKPNO") + ", 풍성품번 : " +ds_SJTZ230_CHECK.GetColumn(i,"PSPNO") + " = " + ds_SJTZ230_CHECK.GetColumn(i,"RTMSG"));
		}
		getSJTZ230();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
		if(ed_Action.Text == "A") fn_Input();
		MainGrid.SetFocus();
		MainGrid.SetCellPos(1);
		MainGrid.ShowEditor();
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_PYVND.BKColor="white";
	cmb_MKYM.BKColor="white";

	if(length(st_PYVND.Text)==0){
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		result =false;
	}
	else if(cmb_MKYM.Value==""){
		cmb_MKYM.BKColor="paleturquoise";
		alert(setPopMessage("4032"));
		setMessage("4032");		
		cmb_MKYM.SetFocus();
		result =false;
	}
	return result;
}

