function fn_Input()
{ 

	if(!validationChk()){
		return;
	}
	if(length(ed_SHPTO.Text)==0){
		ed_SHPTO.Text="0";
	}

	if(checkInsertCount("ds_SJTZ230")== false) return;
	if(ds_SJTZ230.rowcount < 1) {
	 	fn_Search();
	}	
	ds_SJTZ230.AddRow();
	ds_SJTZ230.SetColumn(ds_SJTZ230.currow,"COMPS",ComboBoxPlant.Value);
	ds_SJTZ230.SetColumn(ds_SJTZ230.currow,"PYVND",ed_PYVND.Text);
	ds_SJTZ230.SetColumn(ds_SJTZ230.currow,"SHPTO",ed_SHPTO.Text);
	ds_SJTZ230.SetColumn(ds_SJTZ230.currow,"MAKYM",substr(cmb_MKYM.Value,0,6));
}
function fn_Delete()
{
	if(ds_SJTZ230.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SJTZ230.DeleteRow(ds_SJTZ230.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

