function getMKPNO(sval,tval){

	var sKind		= "getMKPNO";		
	var sMethodName = "service::pscomlib/selectEM000PR3.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM000PR=ds_EM000PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
		if(sval=="MKPNO"){
			sArgument  += " CPRTN=" + quote(tval);
		}
		if(sval=="PSPNO"){
			sArgument  += " PRTNO=" + quote(tval);
		}
		sArgument  += " MAKYM=" + quote(substr(cmb_MKYM.Value,0,6));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_EM000PR.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}

