function ds_SJTZ230_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
//tflag의 ON/OFF에 따라 canColumn의 동작을 제한함.
//품번입력시 업체품번입력을 수행하면서 재동작방지.
	if(strColumnID == "MKPNO"){
//alert("MK=="+tflag);
		if(varNewValue != "" && varOldValue != varNewValue && tflag !="ON" ){
			var tval=getMKPNO(strColumnID, varNewValue);
			if(tval==0){
				//alert(setPopMessage("4001"));  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
				//setMessage("4001");	
				alert("Part Number Cross Ref MA에 미등록된 득의선,품번입니다.");  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
				return false;
			}else{
				var tval=getITNBR(ds_EM000PR.GetColumn(0,"PRTNO"));
				if(tval==0){
					alert("품번확인바랍니다");
					//alert(setPopMessage("1501")); 
					//setMessage("1501");	
					return false;
				}
			}
		}
		tflag="ON";
//alert("MK_END=="+tflag);
	}
	if(strColumnID == "PSPNO"){
//alert("PS=="+tflag);
		if(varNewValue != "" && varOldValue != varNewValue && tflag !="ON" ){
			var tval=getMKPNO(strColumnID, varNewValue);
			if(tval==0){
				//alert(setPopMessage("4001"));  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
				//setMessage("4001");				
				alert("Part Number Cross Ref MA에 미등록된 득의선,품번입니다.");  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
				return false;
			}else{
				var tval=getITNBR(ds_EM000PR.GetColumn(0,"PRTNO"));
				if(tval==0){
					alert("품번확인바랍니다");
					//alert(setPopMessage("1501")); 
					//setMessage("1501");	
					return false;
				}
			}
		}
		tflag="ON";
//alert("PS_END=="+tflag);
	}
}

function ds_SJTZ230_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if( strColumnID == "MKPNO"){
//alert("MK_ONCOL_STR=="+tflag);
		if(varNewValue != ""){
			ds_SJTZ230.SetColumn(nRow,"PSPNO", trim(ds_EM000PR.GetColumn(0,"PRTNO")));
			ds_SJTZ230.SetColumn(nRow,"ITDSC", ds_BM008PR.GetColumn(0,"ITDSC"));
		}else{
			ds_SJTZ230.SetColumn(nRow,"PSPNO", "");
			ds_SJTZ230.SetColumn(nRow,"ITDSC", "");		
		}
		tflag="OFF";
//alert("MK_ONCOL_END=="+tflag);
	}

	if( strColumnID == "PSPNO"){
//alert("PS_ONCOL_STR=="+tflag);
		if(varNewValue != ""){
			ds_SJTZ230.SetColumn(nRow,"MKPNO", trim(ds_EM000PR.GetColumn(0,"CPRTN")));
			ds_SJTZ230.SetColumn(nRow,"ITDSC", ds_BM008PR.GetColumn(0,"ITDSC"));
		}else{
			ds_SJTZ230.SetColumn(nRow,"MKPNO", "");
			ds_SJTZ230.SetColumn(nRow,"ITDSC", "");		
		}
		tflag="OFF";
//alert("PS_ONCOL_END=="+tflag);
	}
}

function ds_SJTZ230_CanRowPosChange(obj,nOldRow,nRow)
{

	if(ds_SJTZ230.GetRowType(ds_SJTZ230.currow)=="update" ||ds_SJTZ230.GetRowType(ds_SJTZ230.currow)=="insert" ){
		var mkpno=ds_SJTZ230.GetColumn(ds_SJTZ230.currow,"MKPNO");
		var pspno=ds_SJTZ230.GetColumn(ds_SJTZ230.currow,"PSPNO");
		var mkqty=ds_SJTZ230.GetColumn(ds_SJTZ230.currow,"MKQTY");

		if(length(mkpno)!=0 && length(pspno)!=0){
			if(length(mkqty)==0||mkqty<1){	//일시불
				//alert(setPopMessage("4034"));  // 이월수량(0 이상)을 입력하세요
				//setMessage("4034");	
				alert("이월수량(0 이상)을 입력하세요.");  //품번확인바랍니다.
				return false;
			}
			if(ds_SJTZ230.FindRow("MKPNO",mkpno)!=ds_SJTZ230.currow){
				var rowNm=parseInt(ds_SJTZ230.FindRow("MKPNO",mkpno))+1;
				alert("[경고]동일한 품번이 등록되어 추가되지 않습니다. 라인번호 : "+ rowNm);
				//alert(setPopMessage("4681")+" 라인번호 : "+ rowNm);
				//setMessage("4681");	
				ds_SJTZ230.DeleteRow(ds_SJTZ230.currow);
				ds_SJTZ230.SelectRow(rowNm-1);
				return false;
			}
		}
		return true;	
	}		
}
//활동구분에서 A이면 추가, Q이면 조회수행
function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); MainGrid.SetFocus(); MainGrid.SetCellPos(1); MainGrid.ShowEditor();	break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");//[확인] 활동CD를 확인바랍니다.
		}
	}
}

//GRID의 마지막입력단에서 Ent입력시 저장TR 수행(값이없을때는 수행안함)
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(MainGrid.GetCellPos()==3 && nChar == 13 && length(ds_SJTZ230.GetColumn(ds_SJTZ230.currow,"MKQTY"))!=0){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}


