Window.Script:
function xChart_OnLoadCompleted(obj){
	xChart0.OpenData("COD_Values", 1, 9);

	For(var i=0;i<9;i++)
	{
		xChart0.Value(0, i) = ds_chart.GetColumn(i,"value");   //value 값 설정
	}
	xChart0.CloseData("COD_Values");

	xChart0.Series[0].color = "#00AF50";

	xChart0.AxisX.Gridlines = True;
	xChart0.AxisY.Gridlines = True;

	// 'In this case we are hardcoding the labels
	// 'but it would be the same if the labels are retrieved from the database
	/* X축 값설정 */
	xChart0.Legend(0) = "TOTAL";
	xChart0.Legend(1) = "공정1";
	xChart0.Legend(2) = "공정2";
	xChart0.Legend(3) = "공정3";
	xChart0.Legend(4) = "공정4";
	xChart0.Legend(5) = "공정5";
	xChart0.Legend(6) = "공정6";
	xChart0.Legend(7) = "공정7";
	xChart0.Legend(8) = "공정8";



	//XChart1.AxisY.LabelValue = 0.01;	                 // 소수점 2자리까지 표현
    xChart0.AxisY.LabelsFormat.CustomFormat = "#,###"; // 출력될 라벨 포멧
	// 'Use the LabelAngle property to show the labels vertically
	//XChart1.AxisX.LabelAngle = 90;

}
