function getMenuList()
{
	var TOP_MENU =getTmenuLev(); //return 값을 변수로 사용함. 2012.04.09 제환복 추가

	http.Sync 	= true;

	Transaction("getUserAuthGroup", "service::user/menuAuthGroup.do", "", "ds_menuAuthGroup=ds_menuAuthGroup", " TOP_MENU=" + quote(TOP_MENU), "");	

	ds_menuAuthGroup.InsertRow(0);
	ds_menuAuthGroup.SetColumn(0,"MENU_ID","");
	ds_menuAuthGroup.SetColumn(0,"MENU_NAME","[전체]");
	cmb_GROUP.index = 0;	


	var sKind		= "getUserAuth";		
	var sMethodName = "service::user/menuAuthList.do";  					
	var sInDataSet  = "";
	var sOutDataSet = "ds_menuAuth=ds_menuAuth";
	var sArgument   = "";
		//sArgument   += " USER_ID=" + quote(USER_ID);
		sArgument   += " TOP_MENU=" + quote(TOP_MENU);//추가
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 function setGroupAuthList(){

	if(!validationChk()) return;

	var sKind		= "setGroupAuthList";						
	var sMethodName = "service::user/saveMenuAuthList.do";    // 서버에서 호출 될 Method 명
	//var sInDataSet  = "ds_menuAuth=ds_menuAuth:U ds_UserAdd=ds_UserAdd:U";               //자료 저장 시 보낼 DataSet 명칭
	var sInDataSet  = "ds_menuAuth=ds_menuAuth ds_UserAdd=ds_UserAdd";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_UserAdd_CHECK=ds_UserAdd_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;


}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	//메뉴선태여부 체크
	ds_menuAuth.UnFilter();
	ds_menuAuth.Filter("USER_YN=='1'");
	chk_SEL.Value=true;

	if(ds_menuAuth.rowcount<1){
		alert("[알림]선택된 메뉴가 없습니다. 확인해주세요.");
		//alert(setPopMessage("1001"));
		//setMessage("1001");	
		result = false;
	}else if(ds_UserAdd.rowcount<1){
		alert("[알림]선택된 사용자뉴가 없습니다. 확인해주세요.");
		//alert(setPopMessage("1001"));
		//setMessage("1001");	
		result = false;
	}

	return result;
}

