/********************************************************************** 
 * 개발일자 : 2010-11-26
 * 내용설명 : 그리드 필터링
 * 주요변수 : 
 **********************************************************************/ 
function groupChange(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cmb_GROUP.Value == ""){
		ds_menuAuth.UnFilter();
		ds_menuAuth.Filter("COMPANY=='" + cmb_COMPS.Value + "'" );		
	} else {
		ds_menuAuth.Filter("STEP1_CODE=='" + cmb_GROUP.Value + "' && COMPANY=='" + cmb_COMPS.Value + "'" );
	}
	chk_SEL.Value=false;
}


function companyChange(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cmb_COMPS.Value == "00"){
		if(cmb_GROUP.Value == ""){
			ds_menuAuth.UnFilter();
		} else {
			ds_menuAuth.Filter("STEP1_CODE=='" + cmb_GROUP.Value + "'" );
		}
	}else if(cmb_COMPS.Value == "0S"){
		if(cmb_GROUP.Value == ""){
			ds_menuAuth.Filter("COMPANY== '00' || COMPANY== 'PS'" );
		} else {
			ds_menuAuth.Filter("STEP1_CODE=='" + cmb_GROUP.Value + "' && (COMPANY== '00' || COMPANY== 'PS')");
		}
	}else if(cmb_COMPS.Value == "0E"){
		if(cmb_GROUP.Value == ""){
			ds_menuAuth.Filter("COMPANY== '00' || COMPANY== 'PE'" );
		} else {
			ds_menuAuth.Filter("STEP1_CODE=='" + cmb_GROUP.Value + "' && (COMPANY== '00' || COMPANY== 'PE')");
		}
	}else {
		if(cmb_GROUP.Value == ""){
			ds_menuAuth.Filter("COMPANY=='" + cmb_COMPS.Value + "'" );
		} else {
			ds_menuAuth.Filter("STEP1_CODE=='" + cmb_GROUP.Value + "' && COMPANY=='" + cmb_COMPS.Value + "'");
		}
	}	
	chk_SEL.Value=false;

}
function ds_UserAdd_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "USER_ID"){//계정등록여부 확인
		if(varNewValue!=""){
			http.Sync = true;
			var sKind		= "getUserList";						
			var sMethodName = "service::user/userList.do";     // 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             // 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_userList=ds_userList";    // 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
				sArgument += " DIONE_ID=" + quote(varNewValue);
			sArgument += " STATUS=" + quote("1");
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_userList.GetRowCount() < 1){
				alert(setPopMessage("4062")); //[확인] 사용자가 존재하지 않습니다.
				setMessage("4062");	
				ds_UserAdd.DeleteRow(ds_UserAdd.currow);	//삭제		
				return false;
			}
		}
	}
	http.usewaitcursor = true;
}

function ds_UserAdd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "USER_ID"){
		if(varNewValue!=""){
			ds_UserAdd.SetColumn(nRow, "USER_NAME", ds_userList.GetColumn(0,"USER_NAME")); // 사용자명
			ds_UserAdd.SetColumn(nRow, "COMPS", ds_userList.GetColumn(0,"PLANT_CD")); // 사업부
			ds_UserAdd.SetColumn(nRow, "TEXT", ds_userList.GetColumn(0,"TEXT")); // 부서명
		}		
		else {
			ds_UserAdd.SetColumn(nRow, "USER_NAME", ""); // 사용자명
			ds_UserAdd.SetColumn(nRow, "COMPS", ""); // 사업부
			ds_UserAdd.SetColumn(nRow, "TEXT", ""); // 부서명
		}
	}
	http.usewaitcursor = true;
}

function chk_SEL_OnClick(obj,strValue)
{
	if(chk_SEL.Value=true){
		ds_menuAuth.Filter("USER_YN=='1'");
	}else{
		ds_menuAuth.UnFilter();
	}
}
