var chkStatus = 0;
function allCehck(obj,nCell,nX,nY,nPivotIndex)
{
	chkStatus = 1 - chkStatus;
	for(i=0;i<ds_menuAuth.rowcount;i++){
		ds_menuAuth.SetColumn(i,"USER_YN",chkStatus) ;
	}
}
