#include "script::lib_script_common.js";


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getMsgListAll();
	gFcOnTopButtonColor("2.1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)					
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getMsgList(){
	var sKind		= "getMsgList";						
	var sMethodName = "service::user/selectMsg.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             	// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_message=ds_message";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " MSG_SEQ=" + quote(ed_msgseq.Value);
	sArgument += " MSG_TEXT=" + quote(ed_msgtext.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 모든리스트 조회 (중복체크용)
 * 주요변수 : 
 **********************************************************************/ 
function getMsgListAll(){
	var sKind		= "getMsgList";						
	var sMethodName = "service::user/selectMsg.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_message=ds_message";    				// 조회 후 돌려 받을 DataSet 명칭
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, "", "");
	ds_messageAll.Copy('ds_message');
	//ds_message.DeleteAll();
	ds_message.ClearData();
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 저장
 * 주요변수 : 
 **********************************************************************/ 
function setMsgList(){
	var sKind		= "setMsgList";						
	var sMethodName = "service::user/msgSave.do";     			// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_message=ds_message:U";     			//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    									// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}





/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 중복 체크
 * 주요변수 : 
 **********************************************************************/ 
function checkid(obj)
{
	if(ds_message.GetRowType(ds_message.row) != 'insert') return ;
	for(i=0;i<ds_messageAll.rowcount; i++){
		if(ds_messageAll.GetColumn(i,"MSG_SEQ") == ed_Vmsgseq.Value) {
			alert(setPopMessage("2040"));	// 20110103l-koowgnojeel, 2040 == 사용중인 메세지들입니다.
			ed_Vmsgseq.SetFocus();
			return;
		}
	}
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getMsgList"){
		// 조회건수가 없을경우
		if(ds_menuList.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setMsgList"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	

}






 /*--------------------------------------------------------------
 * 아래 함수는 콩통함수 이므로 모든 화면에 필요
 * 함수명 변경 불가
 * 버튼 컨트롤
----------------------------------------------------------------*/

function fn_Search()
{
	getMsgList();
}

function fn_Input()
{

	ds_message.AddColumn("MSG_SEQ");
	ds_message.AddColumn("MSG_TEXT");
	ds_message.AddColumn("USER_YN");

	if(checkInsertCount("ds_message")== false) return;
	ds_message.AddRow();
	cb_status.Value = '1';	
}

function fn_Delete()
{
	ds_message.DeleteRow(ds_message.currow);
}

function fn_Save()
{
	setMsgList();

}

function fn_print()
{
	alert(setPopMessage("9999"));
	setMessage("9999");
}

function fn_Excel()
{
	alert(setPopMessage("9999"));
	setMessage("9999");
}

function fn_Help()
{
	alert(setPopMessage("9999"));
	setMessage("9999");
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}
