/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_COMP=ds_ComCode_COMP";
	var sArgument   = "";
		sArgument   += " CLASS='COMP'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	ds_VComCode_COMP.Copy("ds_ComCode_COMP");


	ds_ComCode_COMP.InsertRow(0);
	ds_ComCode_COMP.SetColumn(0,"CODE1","");
	ds_ComCode_COMP.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;	




	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_PLNT.index = 0;		

}



function compChange(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(cb_comp.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_PLNT.index = 0;	
}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 메인 그리드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getUserList(){
	var sKind		= "getUserList";						
	var sMethodName = "service::user/userList.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_userList=ds_userList";    // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " CHK_STATUS='1'";	
	sArgument += " COMPANY=" + quote(cb_comp.Value);	
	sArgument += " PLANT_CD=" + quote(cb_PLNT.Value);	
	sArgument += " USER_ID=" + quote(ed_searchUserId.Value);
	sArgument += " USER_NAME=" + quote(ed_searchUserName.Value);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 서브그리드 조회
 * 주요변수 : 
 **********************************************************************/ 
function ds_userRowPosChange(obj,nOldRow,nRow)
{

	http.Sync = true;

	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             		// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    	// 조회 후 돌려 받을 DataSet 명칭



	var sArgument   = "";
	sArgument += " USER_ID=" + quote(ds_userList.GetColumn(ds_userList.row,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("O");
	sArgument += " COMPANY=" + quote(ds_userList.GetColumn(ds_userList.row,"COMPANY"));	


	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	var sKind		= "getUserCompany";						
	var sMethodName = "service::user/selectUserCompany.do";   // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             		// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_userCompany=ds_userCompany";    	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " COMPANY=" + quote(ds_userList.GetColumn(ds_userList.row,"COMPANY"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 저장
 * 주요변수 : 
 **********************************************************************/ 
function setSsoAccount(){
	//if(!validationChk()) return;
	var sKind		= "setSsoAccount";						
	var sMethodName = "service::user/ssoAccountSave.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ssoaccount=ds_ssoaccount:U";     		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    									// 조회 후 돌려 받을 DataSet 명칭
	var sArgument = "";
	sArgument += " CREATE_USR=" + quote(G_ds_user.GetColumn(0,"USER_ID"));	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getUserList"){
		// 조회건수가 없을경우
		if(ds_userList.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setSsoAccount"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

