 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "사용자 리스트";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|사용자 리스트";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


