#include "script::lib_script_common.js";

function on_LoadCompleted(obj){
	getTitle();	
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	fn_Search();
}

function getYNmrpSystem(){
	http.Sync = true;
	var sKind		= "getYNmrpSystem";						
	var sMethodName = "service::user/selectMngFlag.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SILTM=ds_SILTM ds_mngflag=ds_mngflag";    	// 조회 후 돌려 받을 DataSet 명칭

	var sArgument   = "";
	sArgument += " CLASS=" + quote(radio1.Value);
	sArgument += " COMPS_ORG=" + quote(radio1.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");


}

/********************************************************************** 
 * 내용설명 : 화면저장
 * 주요변수 : 
 **********************************************************************/ 
function setYNmrpSystem(){
	if(!validationChk()) return;
	var sKind		= "setYNmrpSystem";						
	var sMethodName = "service::user/updateMngFlag.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SILTM=ds_SILTM:U ds_mngflag=ds_mngflag:U";     		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    								// 조회 후 돌려 받을 DataSet 명칭
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, "", "callback");
}

/********************************************************************** 
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_mngflag.rowcount(); i++){
		if(length(trim(ds_mngflag.GetColumn(i, "CLASS")))==0){
			alert(setPopMessage("2045"));	//[알림] 소속회사를 입력해 주세요.
			ds_mngflag.row = i;
			return false;
		}
		if(length(trim(ds_mngflag.GetColumn(i, "COMPS")))==0){
			alert(setPopMessage("1510"));	// [경고] 사업부 구분을 확인바랍니다.
			ds_mngflag.row = i;
			return false;
		}
	}
	return true;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getYNmrpSystem"){
		// 조회건수가 없을경우
		if(ds_mngflag.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			//alert(setPopMessage("1004"));
			//setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setYNmrpSystem"){
		//getComCodeClass();
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}


 /*--------------------------------------------------------------
 * 아래 함수는 콩통함수 이므로 모든 화면에 필요
 * 함수명 변경 불가
 * 버튼 컨트롤
----------------------------------------------------------------*/
function fn_Search()
{
	getYNmrpSystem();
}

