function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}
function radio1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_Search();
}

function cb_comp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_mngflag.SetColumn(ds_mngflag.row,"USER_NAME",cb_comp.Text);
}
