mapper.select:
SELECT PK ,EDU_PK ,USER_ID ,STATUS ,REG_DATE ,REG_USER ,MOD_DATE ,MOD_USER ,DESCRIPT FROM TB_EDU_USER WHERE 1 = 1
AND EDU_PK = #{edu_pk}
AND USER_ID = #{emp_id}

mapper.insert:
INSERT
INTO   TB_EDU_USER
       (
             PK
            ,EDU_PK
            ,USER_ID
            ,REG_DATE
            ,REG_USER
            ,MOD_DATE
            ,MOD_USER
            ,DESCRIPT
       )
       VALUES
       (	 (SELECT NVL(MAX(PK),0)+1 FROM TB_EDU_USER)
            , #{edu_pk}
            , #{user_id}
            , SYSDATE
            , #{user_name}
            , SYSDATE
            , #{user_name}
            , #{p_descript}
       )

mapper.update:
UPDATE TB_EDU_USER
SET    STATUS  = 'Y'
WHERE  1       =1
AND    EDU_PK  = #{edu_pk}
AND    USER_ID = #{userId}

mapper.insert:
INSERT
INTO   TB_ATT
       (
              PK
            ,EDU_PK
            ,FILE_NAME
            ,FILE_PATH
            ,FILE_SIZE
            ,FILE_EXT
            ,REG_DATE
            ,REG_USER
            ,MOD_DATE
            ,MOD_USER
            ,ORG_FILE_NAME
            ,USE_IMG
       )
       VALUES
       (	  (SELECT NVL(MAX(PK),0)+1 FROM TB_ATT)
            , #{edu_pk}
            , #{file_name}
            , #{file_path}
            , #{file_size}
            , #{file_ext}
            , SYSDATE
            , #{user_name}
            , SYSDATE
            , #{user_name}
            , #{org_file_name}
            , 'Y'
       )
