mapper.select:
SELECT CLS_CODE ,TRIM(WORK_STLE) AS WORK_STLE ,TRIM(GROUP) AS GROUP ,CASE WHEN 10 > START_TIME THEN '00' || ':0'|| SUBSTR(START_TIME,1,1) WHEN 60 > START_TIME THEN '00' || ':'|| SUBSTR(START_TIME,1,2) WHEN 999 < START_TIME THEN TRIM(SUBSTR(START_TIME,1,2) || ':'|| SUBSTR(START_TIME,3,4)) ELSE TRIM('0' || SUBSTR(START_TIME,1,1) || ':'|| SUBSTR(START_TIME,2,3)) END START_TIME ,CASE WHEN 10 > END_TIME THEN '00' || ':0'|| SUBSTR(END_TIME,1,1) WHEN 60 > END_TIME THEN '00' || ':'|| SUBSTR(END_TIME,1,2) WHEN 999 < END_TIME THEN TRIM(SUBSTR(END_TIME,1,2) || ':'|| SUBSTR(END_TIME,3,4)) ELSE TRIM('0' || SUBSTR(END_TIME,1,1) || ':'|| SUBSTR(END_TIME,2,3)) END END_TIME ,(SUBSTR(APPSTA_DE,1,4) || '-' || SUBSTR(APPSTA_DE,5,2) || '-' || SUBSTR(APPSTA_DE,7,2)) AS APPSTA_DE ,(SUBSTR(APPEND_DE,1,4) || '-' || SUBSTR(APPEND_DE,5,2) || '-' || SUBSTR(APPEND_DE,7,2)) AS APPEND_DE ,TRIM(REFRN) AS REFRN ,REG_USER ,REG_DATE ,MOD_USER ,MOD_DATE ,TRIM(WORK_STLE) AS OLD_WORK_STLE ,TRIM(GROUP) AS OLD_GROUP ,APPSTA_DE AS OLD_APPSTA_DE ,APPEND_DE AS OLD_APPEND_DE FROM PSZDBLIB.FHP050PF WHERE 1 = 1
AND CLS_CODE = #{clsCode}
ORDER BY WORK_STLE,GROUP,APPSTA_DE

SELECT 
      COUNT(*) CNT 
FROM 
     PSZDBLIB.FHP050PF
WHERE CLS_CODE = #{clsCode}
AND WORK_STLE = #{workStle}
AND GROUP = #{group}  
AND APPSTA_DE = #{appstaDe}

SELECT 
      COUNT(*) CNT 
FROM 
     PSZDBLIB.FHP050PF
WHERE CLS_CODE = #{clsCode}
AND WORK_STLE = #{oldWorkStle}
AND GROUP = #{oldGroup}  
AND APPSTA_DE = #{oldAppstaDe}

SELECT 
      COUNT(*) CNT 
FROM 
     PSZDBLIB.FHP050PF
WHERE CLS_CODE = #{clsCode}
AND WORK_STLE = #{oldWorkStle}
AND GROUP = #{oldGroup} 
AND APPSTA_DE <> '${oldAppstaDe}'
AND APPEND_DE <> '${oldAppendDe}'        
AND #{appstaDe} BETWEEN APPSTA_DE AND APPEND_DE

SELECT 
      COUNT(*) CNT 
FROM 
     PSZDBLIB.FHP050PF
WHERE CLS_CODE = #{clsCode}
AND WORK_STLE = #{oldWorkStle}
AND GROUP = #{oldGroup} 
AND APPSTA_DE <> '${oldAppstaDe}'
AND APPEND_DE <> '${oldAppendDe}'        
AND #{appendDe} BETWEEN APPSTA_DE AND APPEND_DE

mapper.insert:
        INSERT INTO  PSZDBLIB.FHP050PF (
				 CLS_CODE
				,WORK_STLE
				,GROUP
				,START_TIME
				,END_TIME
				,APPSTA_DE
				,APPEND_DE									
				,REFRN												
				,REG_USER
				,REG_DATE
        ) values (
                #{clsCode}
               ,#{workStle}
               ,#{group}
               ,#{startTime}
               ,#{endTime}
               ,#{appstaDe}
               ,#{appendDe}
               ,#{refrn}               
               ,#{regUser}               
               ,REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')
        )

mapper.update:
        UPDATE 
               PSZDBLIB.FHP050PF
        SET
              WORK_STLE = #{workStle}
             ,GROUP = #{group}              
			 ,START_TIME = #{startTime}
			 ,END_TIME = #{endTime}
			 ,APPSTA_DE = #{appstaDe}
			 ,APPEND_DE = #{appendDe}
			 ,REFRN = #{refrn}			 
         	 ,MOD_USER = #{modUser}
             ,MOD_DATE = REPLACE(CHAR(DATE(CURRENT DATE), ISO), '-','')
        WHERE CLS_CODE = #{clsCode}
        AND WORK_STLE = #{oldWorkStle}
        AND GROUP = #{oldGroup}  
        AND APPSTA_DE = #{oldAppstaDe}

mapper.delete:
DELETE 
FROM 
     PSZDBLIB.FHP050PF
WHERE CLS_CODE = #{clsCode}
AND WORK_STLE = #{workStle}
AND GROUP = #{group} 
AND APPSTA_DE = #{appstaDe}
