create or replace PROCEDURE PROC_MERGE_EMP(
  RTN_CD OUT INTEGER
, RTN_MSG OUT VARCHAR) 
 IS 
 V_EMP_NUMBER VARCHAR(100);
 V_CLS_CODE  VARCHAR(100);
 V_LOC_CODE  VARCHAR(100);
 V_TEAM_CODE VARCHAR(100);
 V_EMP_NAME  VARCHAR(100);
 V_EMP_ID    VARCHAR(100);
BEGIN

  --RTN_CD  := 1;
  --RTN_MSG := '처리되었습니다.';
  
   DECLARE
     CURSOR C1 IS
       SELECT EMP_NUMBER, CLS_CODE, LOC_CODE, TEAM_CODE, EMP_NAME, EMP_ID 
          FROM DNKR_EMP_TMP ORDER BY EMP_NUMBER ASC;
     REC1 C1%ROWTYPE;
     BEGIN
       OPEN C1;
         LOOP
          FETCH C1 INTO REC1;
            EXIT WHEN C1%NOTFOUND;
            
                V_EMP_NUMBER := REC1.EMP_NUMBER;
                V_CLS_CODE   := REC1.CLS_CODE;
                V_LOC_CODE   := REC1.LOC_CODE;
                V_TEAM_CODE  := REC1.TEAM_CODE;
                V_EMP_NAME   := REC1.EMP_NAME;
                V_EMP_ID     := REC1.EMP_ID;
                
                MERGE INTO DNKR_EMP A
                  USING DUAL
                  ON(EMP_NUMBER = V_EMP_NUMBER)
                  WHEN MATCHED THEN
                    UPDATE SET
                        A.CLS_CODE  = V_CLS_CODE
                       ,A.LOC_CODE  = V_LOC_CODE
                       ,A.TEAM_CODE = V_TEAM_CODE
                       ,A.EMP_NAME  = V_EMP_NAME
                       ,A.EMP_ID    = V_EMP_ID
                  WHEN NOT MATCHED THEN
                    INSERT ( A.EMP_NUMBER
                           , A.CLS_CODE
                           , A.LOC_CODE
                           , A.TEAM_CODE
                           , A.AUTH_CODE
                           , A.EMP_NAME
                           , A.EMP_EMAIL
                           , A.EMP_ID
                           , A.EMP_SYNC_DATE
                           , A.EMP_USE
                           , A.EMP_PWD
                           , A.EMP_PRIVATE_AUTH
                           , A.IN_OUT_TYPE
                         ) VALUES (
                           V_EMP_NUMBER
                         , V_CLS_CODE
                         , V_LOC_CODE
                         , V_TEAM_CODE
                         , null
                         , V_EMP_NAME
                         , NULL
                         , V_EMP_ID
                         , SYSDATE
                         , 'Y'
                         , null
                         , 'N'
                         , 'I');
                         
         END LOOP;
       CLOSE C1;
   
   END;
 
 RTN_CD := 1;
 RTN_MSG := '성공하였습니다.';
 --COMMIT;
  
  EXCEPTION
  WHEN OTHERS THEN
    RTN_CD := 0;
    RTN_MSG := SQLERRM;
    --ROLLBACK;
 
 END PROC_MERGE_EMP;

create or replace PROCEDURE PROC_MERGE_TEAM(
  IN_RTN_CD OUT NUMBER
, IN_RTN_MSG OUT VARCHAR2) 
 IS 
 V_RESULT NUMBER;
 V_CD_DEPT VARCHAR2(12);
 V_CD_COMPANY VARCHAR2(7);
 V_NM_DEPT VARCHAR2(50);
 V_CD_BIZAREA VARCHAR2(7);
 V_CD_CC VARCHAR2(12);
 V_CLS_CODE VARCHAR2(45);
 V_CLS_RESULT NUMBER;
 V_LOC_CODE VARCHAR2(45);
 V_LOC_RESULT NUMBER;
 V_TEAM_CODE VARCHAR2(45);
 V_RTN_COUNT NUMBER;
BEGIN

 V_RTN_COUNT := 0;
 IN_RTN_CD := 0;
 IN_RTN_MSG := 'OK';
 
 --덴소 MA_DEPT 테이블의 데이터를 MA_DEPT_TMP 로 가져온뒤에 프로시저 실행
 -- MA_DEPT_TMP 에 CD_DEPT 데이터가 부서(팀) DNKR_RELATION_TEAM 테이블의 RLTN_CODE 에 있는지 확인 후 없으면 INSERT 실행
 -- 1. DNKR_RELATION_TEAM 에 INSERT
 -- 1-1. TEAM_CODE 새로생성
 -- 1-2. CLS_CODE 는 DNKR_RELATION_CLASS 에서 RLTN_CODE = CD_COMPANY 인 CLS_CODE 저장
 -- 1-3. LOC_CODE 는 DNKR_RELATION_LOCATION 에서 CLS_CODE = CD_COMPANY 이고 RLTN_CODE는 CD_BIZAREA 인 LOC_CODE 저장
 -- 1-4. TEAM_USE 는 Y
 -- 1-5. AUTH_CODE 는 NULL
 -- 1-6. RLTN_CODE 는 CD_DEPT
 -- 1-7. 단 CLS_CODE 와 LOC_CODE 가 없으면 INSERT 안함
 -- 2. 조직조명 언어별로 3개 INSERT (en, ja, kr)
 -- 2-1. CMC_CODE 은 en, ja, kr
 -- 2-2. REL_CODE 는 새로 생성된 TEAM_CODE
 -- 2-3. REL_NAME 은 NM_DEPT
  
  DECLARE
     CURSOR C1 IS
       SELECT CD_DEPT
            , CD_COMPANY
            , NM_DEPT
            , CD_BIZAREA
            , CD_CC
         FROM MA_DEPT_TMP ORDER BY CD_DEPT ASC;
     REC1 C1%ROWTYPE;
     BEGIN
       OPEN C1;
         LOOP
          FETCH C1 INTO REC1;
            EXIT WHEN C1%NOTFOUND;
                
                V_CD_DEPT     := REC1.CD_DEPT;
                V_CD_COMPANY  := REC1.CD_COMPANY;
                V_NM_DEPT     := REC1.NM_DEPT;
                V_CD_BIZAREA  := REC1.CD_BIZAREA;
                V_CD_CC       := REC1.CD_CC;
                
                -- 부서테이블에 데이터가 있는지 확인
                SELECT COUNT(*)
                  INTO V_RESULT
                  FROM DNKR_RELATION_TEAM 
                 WHERE RLTN_CODE = V_CD_DEPT;
                
                -- 데이터가 없으면 INSERT
                IF V_RESULT < 1 THEN
                  
                  --사업부, 사업장에 코드가 있는지 확인
                  SELECT COUNT(*)
                    INTO V_CLS_RESULT
                    FROM DNKR_RELATION_CLASS
                   WHERE RLTN_CODE = V_CD_COMPANY;
                  
                  IF V_CLS_RESULT > 0 THEN
                    SELECT CLS_CODE
                      INTO V_CLS_CODE
                      FROM DNKR_RELATION_CLASS
                     WHERE RLTN_CODE = V_CD_COMPANY;
                  END IF;
                  
                  SELECT COUNT(*)
                    INTO V_LOC_RESULT
                    FROM DNKR_RELATION_LOCATION
                   WHERE CLS_CODE = V_CLS_CODE
                     AND RLTN_CODE = V_CD_BIZAREA;
                     
                  V_RESULT := V_CLS_RESULT * V_LOC_RESULT;
                  
                  --연결된 코드가 있으면 INSERT
                  IF V_RESULT > 0 THEN
                  
                    --부서 테이블 TEAM 코드 새로 생성
                    V_TEAM_CODE := 'TEAM' || lpad(DNKR_RELATION_TEAM_SEQ.NEXTVAL, 6, '0');
                   
                    --부서 테이블에 INSERT
                           TEAM_CODE
                         , CLS_CODE
                         , LOC_CODE
                         , TEAM_USE
                         , AUTH_CODE
                         , RLTN_CODE
                         ) VALUES(
                           V_TEAM_CODE
                         , (SELECT CLS_CODE FROM DNKR_RELATION_CLASS WHERE RLTN_CODE = V_CD_COMPANY)
                         , (SELECT LOC_CODE FROM DNKR_RELATION_LOCATION WHERE CLS_CODE = V_CLS_CODE AND RLTN_CODE = V_CD_BIZAREA)
                         , 'Y'
                         , NULL
                         , V_CD_DEPT
                         );
                    
                    --조직도명 에 INSERT (en)
                           CMC_CODE
                         , REL_CODE
                         , REL_NAME
                         ) VALUES (
                           'en'
                         , V_TEAM_CODE
                         , V_NM_DEPT
                         );
                    
                    --조직도명 에 INSERT (ja)     
                           CMC_CODE
                         , REL_CODE
                         , REL_NAME
                         ) VALUES (
                           'ja'
                         , V_TEAM_CODE
                         , V_NM_DEPT
                         );
                         
                    --조직도명 에 INSERT (kr)
                           CMC_CODE
                         , REL_CODE
                         , REL_NAME
                         ) VALUES (
                           'kr'
                         , V_TEAM_CODE
                         , V_NM_DEPT
                         );
                    -- INSERT 성공 카운트 증가
                    V_RTN_COUNT := V_RTN_COUNT + 1;
                  END IF;
                END IF;
                --INSERT 가 안되더라도 정상으로 진행되면 IN_RTN_CD 1로 반환
                IN_RTN_CD := 1;                      
         END LOOP;
       CLOSE C1;
   END;
   
   IN_RTN_MSG := V_RTN_COUNT || '건 입력 성공';
   RETURN;
   
   EXCEPTION
  WHEN OTHERS THEN
    IN_RTN_CD  := 0;
    IN_RTN_MSG := '부서 정보 업데이트가 실패 하였습니다..' || TO_CHAR(SQLCODE) || '  ' || SQLERRM;
  
 END PROC_MERGE_TEAM;